CREATE OR REPLACE PROCEDURE pls_consistir_dados_pagamento ( dt_inicio_vigencia_p text, dt_final_vigencia_p text, nm_usuario_p text, ie_consistir_p INOUT text) AS $body$
DECLARE


dt_inicio_w	timestamp;
dt_final_w	timestamp;


BEGIN
dt_inicio_w	:= trunc(to_date(substr(dt_inicio_vigencia_p,1,10)));
dt_final_w	:= fim_dia(coalesce(to_date(substr(dt_final_vigencia_p,1,10)),dt_inicio_w));

if (dt_inicio_w > dt_final_w) then
	ie_consistir_p	:= 'S';
else
	ie_consistir_p	:= 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
