CREATE OR REPLACE PROCEDURE pls_consistir_dados_reembolso ( dt_reembolso_p pls_conta.dt_emissao%type, nr_seq_segurado_p pls_conta.nr_seq_segurado%type, nr_documento_p pls_conta.nr_documento%type, cd_pessoa_fisica_p pls_conta.cd_pessoa_fisica%type, cd_cgc_p pls_conta.cd_cgc%type, nr_seq_conta_p pls_conta.nr_sequencia%type, ie_opcao_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ds_mensagem_p INOUT text) AS $body$
DECLARE



/*
ie_opcao_p

D - Data irá validar se a data de emissao ca conta permite o reembolso
R - Irá verificar se não existe nenhum documento do mesmo beneficiário com o mesmo número
*/
qt_dias_permitidos_w		integer;
nr_seq_conta_w			pls_conta.nr_sequencia%type;

BEGIN

if (ie_opcao_p 	= 'D') then
	qt_dias_permitidos_w	:= obter_valor_param_usuario(1229, 13, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p);
	if (coalesce(qt_dias_permitidos_w,0) <> 0) then
		if (qt_dias_permitidos_w	< (trunc(clock_timestamp()) - trunc(to_date(dt_reembolso_p)))::numeric ) then
			ds_mensagem_p	:= 'Data do reembolso anterior a data permitida! [13]';
		end if;
	end if;
elsif (ie_opcao_p	= 'R') then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_conta_w
	from	pls_conta		a,
		pls_protocolo_conta	b
	where	a.nr_documento		= nr_documento_p
	and	((a.cd_pessoa_fisica	= cd_pessoa_fisica_p) or (coalesce(a.cd_pessoa_fisica::text, '') = '' and coalesce(cd_pessoa_fisica_p::text, '') = ''))
	and	((a.cd_cgc		= cd_cgc_p)	or (coalesce(a.cd_cgc::text, '') = '' and coalesce(cd_cgc_p::text, '') = ''))
	and	((a.nr_sequencia	!= nr_seq_conta_p) or (coalesce(nr_seq_conta_p::text, '') = ''))
	and	a.nr_seq_protocolo	= b.nr_sequencia
	and	b.nr_seq_segurado	= nr_seq_segurado_p;

	if (nr_seq_conta_w IS NOT NULL AND nr_seq_conta_w::text <> '') then
		ds_mensagem_p	:= 'A conta '||nr_seq_conta_w||' possuí o mesmo número de documento registrado deseja continuar?';
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
