SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_div_fatura ( nr_seq_fatura_p ptu_fatura.nr_sequencia%type) AS $body$
DECLARE

			
		
ie_div_valor_item_w	varchar(1) := 'N';
		

BEGIN

	select  CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_div_valor_item_w
	from  	pls_conta_proc    a,
		pls_conta    b,
		ptu_fatura c
	where  b.nr_seq_fatura  = nr_seq_fatura_p
	and 	b.nr_seq_fatura = c.nr_sequencia
	and  	a.nr_seq_conta = b.nr_sequencia
	and  	a.ie_status <> 'M'
	and (coalesce(a.vl_liberado_hi,0) + coalesce(a.vl_liberado_co,0) + coalesce(a.vl_liberado_material,0) +
		coalesce(a.vl_lib_taxa_servico,0) + coalesce(a.vl_lib_taxa_co,0) + coalesce(a.vl_lib_taxa_material,0)) != a.vl_liberado;

	update ptu_fatura
	set   ie_div_valor = ie_div_valor_item_w
	where nr_sequencia = nr_seq_fatura_p;
	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
