SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_documentos_prop ( nr_seq_proposta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_benef_proposta_w		bigint;
cd_pessoa_fisica_w		varchar(10);
ie_titular_w			varchar(10);
dt_nascimento_w			timestamp;
qt_idade_w			bigint;
ie_tipo_proposta_w		varchar(10);
nr_seq_parentesco_w		bigint;
ie_tipo_contratacao_w		varchar(10);
nr_seq_regra_doc_prop_w		bigint;
qt_registros_w			bigint;
nr_seq_tipo_documento_w		bigint;
qt_registros_inco_w		bigint;
ie_tipo_processo_w		varchar(1);

C01 CURSOR FOR
	SELECT	b.nr_sequencia,
		b.cd_beneficiario,
		a.dt_nascimento,
		b.nr_seq_parentesco,
		CASE WHEN coalesce(nr_seq_titular::text, '') = '' THEN CASE WHEN coalesce(nr_seq_titular_contrato::text, '') = '' THEN 'T'  ELSE 'D' END   ELSE 'D' END
	from	pls_proposta_beneficiario	b,
		pessoa_fisica			a
	where	a.cd_pessoa_fisica	= b.cd_beneficiario
	and	b.nr_seq_proposta	= nr_seq_proposta_p
	and	coalesce(b.dt_cancelamento::text, '') = '';

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_regra_doc_proposta
	where	ie_situacao		= 'A'
	and	((ie_tipo_proposta	= ie_tipo_proposta_w) or (coalesce(ie_tipo_proposta::text, '') = ''))
	and	((ie_tipo_contratacao	= ie_tipo_contratacao_w) or (coalesce(ie_tipo_contratacao::text, '') = ''))
	and	((ie_titularidade	= ie_titular_w) or (ie_titularidade = 'A'))
	and	((nr_seq_parentesco	= nr_seq_parentesco_w) or (coalesce(nr_seq_parentesco::text, '') = ''))
	and	((ie_tipo_processo	= ie_tipo_processo_w) or (ie_tipo_processo = 'A'))
	and	qt_idade_w between coalesce(qt_idade_inicial,qt_idade_w) and coalesce(qt_idade_final,qt_idade_w);

C03 CURSOR FOR
	SELECT	nr_seq_tipo_documento
	from	pls_regra_proposta_doc
	where	nr_seq_regra	= nr_seq_regra_doc_prop_w;


BEGIN

delete	from	pls_proposta_inconsist_doc
where	nr_seq_proposta = nr_seq_proposta_p
and	coalesce(dt_liberacao::text, '') = '';

select	ie_tipo_proposta,
	ie_tipo_contratacao,
	ie_tipo_processo
into STRICT	ie_tipo_proposta_w,
	ie_tipo_contratacao_w,
	ie_tipo_processo_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_p;

open C01;
loop
fetch C01 into
	nr_seq_benef_proposta_w,
	cd_pessoa_fisica_w,
	dt_nascimento_w,
	nr_seq_parentesco_w,
	ie_titular_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_idade_w	:= trunc(months_between(clock_timestamp(), dt_nascimento_w) / 12);

	open C02;
	loop
	fetch C02 into
		nr_seq_regra_doc_prop_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		open C03;
		loop
		fetch C03 into
			nr_seq_tipo_documento_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin
			qt_registros_inco_w := 0;
			select	count(1)
			into STRICT	qt_registros_w
			from	pls_tipo_documento_pf	b,
				pls_documento_pf	a
			where	a.nr_seq_tipo_doc_pf	= b.nr_sequencia
			and	b.cd_pessoa_fisica	= cd_pessoa_fisica_w
			and	b.nr_seq_tipo_documento	= nr_seq_tipo_documento_w;

			begin
			select	count(1)
			into STRICT	qt_registros_inco_w
			from	pls_proposta_inconsist_doc
			where	nr_seq_proposta		= nr_seq_proposta_p
			and	nr_seq_benef_proposta	= nr_seq_benef_proposta_w
			and	nr_seq_tipo_documento	= nr_seq_tipo_documento_w;
			exception
			when others then
				qt_registros_inco_w := 0;
			end;

			if	(qt_registros_w = 0 AND qt_registros_inco_w = 0) then
				insert into pls_proposta_inconsist_doc(	nr_sequencia, nr_seq_proposta,
							nr_seq_benef_proposta, nr_seq_tipo_documento, dt_atualizacao, nm_usuario,
							dt_atualizacao_nrec, nm_usuario_nrec, dt_liberacao, nm_usuario_liberacao)
					values (	nextval('pls_proposta_inconsist_doc_seq'), nr_seq_proposta_p,
							nr_seq_benef_proposta_w, nr_seq_tipo_documento_w, clock_timestamp(), nm_usuario_p,
							clock_timestamp(), nm_usuario_p, null, null);
			end if;
			end;
		end loop;
		close C03;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
