SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_dt_receb_fatura (dt_recebimento_p timestamp, ie_consistencia_p INOUT text) AS $body$
DECLARE


dt_recebimento_w	           	timestamp;
dt_referencia_w		            	timestamp;
ie_permite_imp_fat_data_fut_w 		pls_parametros.ie_permite_imp_fat_data_fut%type;


BEGIN
dt_recebimento_w              	:= trunc(dt_recebimento_p,'dd');
dt_referencia_w	             	:= trunc(clock_timestamp(),'dd');
ie_consistencia_p            	:= 'N';
ie_permite_imp_fat_data_fut_w 	:= 'N';

select coalesce(max(ie_permite_imp_fat_data_fut),'N')
  into STRICT ie_permite_imp_fat_data_fut_w
  from PLS_PARAMETROS
  where cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

if (ie_permite_imp_fat_data_fut_w = 'N' and dt_recebimento_w > dt_referencia_w) then
	ie_consistencia_p := 'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
