CREATE OR REPLACE PROCEDURE pls_consistir_duplic_cada_pf ( cd_pessoa_fisica_p text, nm_pessoa_fisica_p text, dt_nascimento_p timestamp, nr_identidade_p text, nm_usuario_p text, ie_consite_p INOUT text) AS $body$
DECLARE



nm_pessoa_param_w	varchar(60);
dt_nasc_param_w		timestamp;
nm_mae_param_w		varchar(60);

cd_pessoa_duplic_w	varchar(10);
nm_pessoa_duplic_w	varchar(60);
nr_pront_duplic_w	bigint;

ie_consiste_w		varchar(10);
ie_somente_nome_w	varchar(255);

c01 CURSOR FOR
	SELECT	cd_pessoa_fisica
	from (	SELECT	a.cd_pessoa_fisica
			from	pessoa_fisica a
			where	a.cd_pessoa_fisica	<> cd_pessoa_fisica_p
			and	a.nm_pessoa_pesquisa	= nm_pessoa_param_w
			and	coalesce(nr_identidade_p,'0')	= '0'
			and (coalesce(ie_somente_nome_w,'N') = 'N')
			
union

			select	a.cd_pessoa_fisica
			from	pessoa_fisica a
			where	a.cd_pessoa_fisica	<> cd_pessoa_fisica_p
			and	a.nm_pessoa_pesquisa	= nm_pessoa_param_w
			and	a.dt_nascimento		= dt_nasc_param_w
			and	coalesce(nr_identidade_p,'0')	= '0'
			
union

			select	a.cd_pessoa_fisica
			from	pessoa_fisica a
			where	a.cd_pessoa_fisica	<> cd_pessoa_fisica_p
			and	upper(a.nm_pessoa_fisica)	= upper(nm_pessoa_fisica_p)
			and	a.dt_nascimento		= dt_nasc_param_w
			and	a.nr_identidade		= nr_identidade_p) alias6
	group by cd_pessoa_fisica;


BEGIN

ie_somente_nome_w := obter_param_usuario(5, 162, Obter_perfil_Ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_somente_nome_w);

nm_pessoa_param_w	:= substr(padronizar_nome(nm_pessoa_fisica_p),1,60);
dt_nasc_param_w		:= dt_nascimento_p;

delete	FROM W_PLS_DUPLICIDADE_PF
where	nm_usuario	= nm_usuario_p;

ie_consiste_w	:= 'N';

open c01;
loop
fetch c01 into
	cd_pessoa_duplic_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ie_consiste_w	:= 'S';
	insert into W_PLS_DUPLICIDADE_PF(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
			cd_pessoa_fisica)
	values (	nextval('w_pls_duplicidade_pf_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
			cd_pessoa_duplic_w);

	end;
end loop;
close c01;

ie_consite_p	:= ie_consiste_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
