SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_elegib_web ( nr_seq_conta_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_segurado_w		bigint;
ie_situacao_atend_w		varchar(1);
nr_seq_prestador_w		varchar(20);
nr_seq_pagador_w		bigint;
dt_rescisao_w			timestamp;
dt_emissao_w			timestamp;
dt_limite_utilizacao_w		timestamp;
dt_rescisao_contrato_w		timestamp;
nr_seq_contrato_w		bigint;
ie_inadiplente_w		varchar(5);
dt_suspensao_w			timestamp;
dt_reativacao_w			timestamp;

/*  Obter dados  da conta*/

BEGIN

begin
	select	nr_seq_segurado,
		trunc(dt_emissao),
		nr_seq_prestador
	into STRICT	nr_seq_segurado_w,
		dt_emissao_w,
		nr_seq_prestador_w
	from	pls_conta
	where	nr_sequencia     = nr_seq_conta_p;
exception
when others then
	nr_seq_segurado_w := 0;
end;

/* Obter dados do segurado */

begin
	select	dt_rescisao,
		fim_dia(dt_limite_utilizacao),
		coalesce(ie_situacao_atend,'A'),
		nr_seq_pagador,
		nr_seq_contrato
	into STRICT	dt_limite_utilizacao_w,
		dt_rescisao_w,
		ie_situacao_atend_w,
		nr_seq_pagador_w,
		nr_seq_contrato_w
	from	pls_segurado
	where	nr_sequencia = nr_seq_segurado_w;
exception
when others then
	nr_seq_segurado_w := 0;
end;

begin
select	trunc(dt_suspensao),
	trunc(dt_reativacao)
into STRICT	dt_suspensao_w,
	dt_reativacao_w
from	pls_contrato_pagador
where	nr_sequencia = nr_seq_pagador_w;
exception
when others then
	dt_suspensao_w		:= null;
	dt_reativacao_w		:= null;
end;

/* Obter dados do contrato */

begin
	select	dt_rescisao_contrato
	into STRICT	dt_rescisao_contrato_w
	from	pls_contrato
	where	nr_sequencia = nr_seq_contrato_w;
exception
when others then
	dt_rescisao_contrato_w := null;
end;

/* Obter se o pagador do beneficiário está inadiplente*/

ie_inadiplente_w := pls_obter_se_inadimplente(nr_seq_pagador_w,nr_seq_segurado_w);

/* 1009 - Beneficiário com pagamento em aberto*/

if (ie_inadiplente_w = 'S') then
	CALL pls_gravar_glosa_tiss('1009', null, 'IA',
		nr_seq_conta_p, 'C', nr_seq_prestador_w,
		'', '', nm_usuario_p,
		cd_estabelecimento_p, null);
end if;

/* 1016 - Beneficiário com atendimento suspenso*/

if (ie_situacao_atend_w = 'S') then
	CALL pls_gravar_glosa_tiss('1016', null, 'IA',
		nr_seq_conta_p, 'C', nr_seq_prestador_w,
		'', '', nm_usuario_p,
		cd_estabelecimento_p, null);
end if;

/* 1006 - Atendimento após o desligamento do Beneficiário*/

if (dt_rescisao_w IS NOT NULL AND dt_rescisao_w::text <> '') and (dt_emissao_w > dt_limite_utilizacao_w) then
	CALL pls_gravar_glosa_tiss('1006',
		'Data de emissão da conta : ' || to_char(dt_emissao_w,'dd/mm/yyyy') || '. Data de rescisão : '||to_char(dt_rescisao_w,'dd/mm/yyyy'), 'IA',
		nr_seq_conta_p, 'C', nr_seq_prestador_w,
		'', '', nm_usuario_p,
		cd_estabelecimento_p, null);
end if;

/* 1018 - Empresa do beneficiário suspensa / excluída */

if (dt_emissao_w > dt_rescisao_contrato_w) then
	CALL pls_gravar_glosa_tiss('1018',
		'[1] - Contrato rescindido.'||chr(13)||chr(10)||
		'Data de emissão da conta : ' || to_char(dt_emissao_w,'dd/mm/yyyy') || '. Data de rescisão do contrato : '||to_char(dt_rescisao_contrato_w,'dd/mm/yyyy'), 'IA',
		nr_seq_conta_p, 'C', nr_seq_prestador_w,
		'', '', nm_usuario_p,
		cd_estabelecimento_p, null);
end if;

/* 1018 - Empresa do beneficiário suspensa / excluída */

if	((dt_suspensao_w IS NOT NULL AND dt_suspensao_w::text <> '') and (coalesce(dt_reativacao_w::text, '') = '')  and (dt_emissao_w > dt_suspensao_w))  then
	 CALL pls_gravar_glosa_tiss('1018',
		'[2] - Pagador suspenso.'||chr(13)||chr(10)||
		'Data de emissão da conta : ' || to_char(dt_emissao_w,'dd/mm/yyyy') || '. Data de suspensão do pagador : '||to_char(dt_suspensao_w,'dd/mm/yyyy'), 'IA',
		nr_seq_conta_p, 'C', nr_seq_prestador_w,
		'', '', nm_usuario_p,
		cd_estabelecimento_p, null);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consistir_elegib_web ( nr_seq_conta_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

