CREATE OR REPLACE PROCEDURE pls_consistir_endereco_boleto ( cd_pessoa_fisica_p text, cd_cgc_p text, ie_endereco_boleto_p text, nr_seq_tipo_compl_adic_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE




