CREATE OR REPLACE PROCEDURE pls_consistir_glosa_item_conta ( nr_seq_conta_p bigint, nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint, nm_usuario_p text) AS $body$
DECLARE

				 
ie_permite_glosar_w	varchar(1)	:= 'N';


BEGIN 
 
if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then 
	select		CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END  
	into STRICT		ie_permite_glosar_w 
	from		pls_conta_glosa_ocorrencia_v 
	where		nr_seq_conta	= nr_seq_conta_p 
	order by	nr_sequencia;
elsif (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then 
	select		CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END  
	into STRICT		ie_permite_glosar_w 
	from		pls_conta_glosa_ocorrencia_v 
	where		nr_seq_proc	= nr_seq_conta_proc_p 
	order by	nr_sequencia;
elsif (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then 
	select		CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END  
	into STRICT		ie_permite_glosar_w 
	from		pls_conta_glosa_ocorrencia_v 
	where		nr_seq_mat	= nr_seq_conta_mat_p 
	order by	nr_sequencia;
end if;
 
if (ie_permite_glosar_w = 'N') then 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(324951);
end if;
 
end;
$body$
