CREATE OR REPLACE PROCEDURE pls_consistir_idade_estip_pag ( nr_seq_proposta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
cd_pagador_pf_w			varchar(10);
cd_estipulante_pf_w		varchar(10);
qt_idade_estip_w		bigint;
qt_idade_pagador_w		bigint;
ie_consistir_estipulante_w	varchar(1);
ie_consistir_pagador_w		varchar(1);
ds_erro_w			varchar(4000);
ie_emancipado_w			varchar(10);

C01 CURSOR FOR 
	SELECT	cd_pagador 
	from	pls_proposta_pagador 
	where	nr_seq_proposta	= nr_seq_proposta_p 
	and	coalesce(cd_cgc_pagador::text, '') = '';


BEGIN 
 
ie_consistir_estipulante_w	:= coalesce(obter_valor_param_usuario(1232, 52, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p), 'N');
ie_consistir_pagador_w		:= coalesce(obter_valor_param_usuario(1232, 108, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p), 'N');
 
select	max(cd_estipulante) 
into STRICT	cd_estipulante_pf_w 
from	pls_proposta_adesao 
where	nr_sequencia	= nr_seq_proposta_p;
 
if (ie_consistir_estipulante_w	= 'S') and (cd_estipulante_pf_w IS NOT NULL AND cd_estipulante_pf_w::text <> '') then 
	select	coalesce(ie_emancipado,'N') 
	into STRICT	ie_emancipado_w 
	from	pessoa_fisica 
	where	cd_pessoa_fisica	= cd_estipulante_pf_w;
	 
	qt_idade_estip_w	:= obter_idade_pf(cd_estipulante_pf_w,clock_timestamp(),'A');
	if (qt_idade_estip_w	< 18) and (ie_emancipado_w	= 'N') then 
		ds_erro_w	:= ds_erro_w || wheb_mensagem_pck.get_texto(280472, 'CD_ESTIPULANTE_P=' || obter_nome_pf(cd_estipulante_pf_w));
	end if;
end if;
 
if (ie_consistir_pagador_w = 'S') then 
	open C01;
	loop 
	fetch C01 into 
		cd_pagador_pf_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		select	coalesce(ie_emancipado,'N') 
		into STRICT	ie_emancipado_w 
		from	pessoa_fisica 
		where	cd_pessoa_fisica	= cd_pagador_pf_w;
		 
		qt_idade_pagador_w	:= obter_idade_pf(cd_pagador_pf_w,clock_timestamp(),'A');
		if (qt_idade_pagador_w	< 18) and (ie_emancipado_w	= 'N') then 
			ds_erro_w	:= ds_erro_w || wheb_mensagem_pck.get_texto(280473, 'CD_PAGADOR_P=' || obter_nome_pf(cd_pagador_pf_w));
		end if;
		end;
	end loop;
	close C01;
end if;
 
ds_erro_p	:= ds_erro_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
