SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_lanc_defesa ( ie_tipo_defesa_p text, nr_seq_defesa_p bigint, nr_seq_impugnacao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


qt_defesa_tipo_w		bigint;
ds_erro_w			varchar(255);
ie_consistir_status_w		varchar(1)	:= 'N';
qt_registros_w			bigint;


BEGIN

select	obter_valor_param_usuario(1238,2,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_p)
into STRICT	ie_consistir_status_w
;

if (ie_consistir_status_w	= 'N') then
	select	count(*)
	into STRICT	qt_registros_w
	from	pls_impugnacao_defesa
	where	nr_sequencia		<> nr_seq_defesa_p
	and	nr_seq_impugnacao	= nr_seq_impugnacao_p
	and	coalesce(dt_cancelamento::text, '') = ''
	and	ie_status	= 'A';

	if (qt_registros_w > 0) then
		ds_erro_w	:= wheb_mensagem_pck.get_texto(280292);
	end if;
end if;

select	count(*)
into STRICT	qt_defesa_tipo_w
from	pls_impugnacao_defesa
where	ie_tipo_defesa	= ie_tipo_defesa_p
and	nr_sequencia	<> nr_seq_defesa_p
and	nr_seq_impugnacao	= nr_seq_impugnacao_p
and	coalesce(dt_cancelamento::text, '') = '';

if (qt_defesa_tipo_w	> 0) then
	ds_erro_w	:= wheb_mensagem_pck.get_texto(280293, 'IE_TIPO_DEFESA_P=' || obter_valor_dominio(2531,ie_tipo_defesa_p));
end if;

ds_erro_p	:= substr(ds_erro_w,1,length(ds_erro_w)-2);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
