SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_limitacao_mat ( nr_seq_segurado_p bigint, nr_seq_guia_mat_p bigint, nr_seq_conta_mat_p bigint, nr_seq_requisicao_mat_p bigint, nr_seq_material_p bigint, qt_solicitada_p bigint, nr_seq_guia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_limita_w			varchar(1)	:= 'N';
ie_liberado_w			varchar(1)	:= 'N';
qt_permitida_w			double precision;
ie_periodo_w			varchar(2);
ie_origem_w			varchar(1);
qt_meses_intervalo_w		integer;
nr_seq_contrato_w		bigint;
nr_seq_plano_w			bigint;
nr_seq_limitacao_mat_w		bigint;
nr_seq_tipo_limitacao_w		bigint;
nr_seq_prestador_w		bigint;
ie_tipo_consistencia_w		varchar(2);
nr_seq_conta_w			bigint;
ie_tipo_operacao_w		varchar(1);
ie_tipo_periodo_w		varchar(1);
nr_seq_requisicao_w		bigint;
nr_seq_limitacao_w		bigint;
nr_seq_intercambio_w		bigint;
nr_seq_estrut_mat_w		bigint;
ie_tipo_protocolo_w		pls_protocolo_conta.ie_tipo_protocolo%type;
ie_qt_familia_w			pls_limitacao.ie_qt_familia%type;
nr_seq_titular_w		pls_segurado.nr_seq_titular%type;
qt_pessoas_w			bigint;

c01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.ie_limitacao,
		a.nr_seq_estrut_mat,
		a.nr_seq_tipo_limitacao,
		c.qt_permitida,
		c.qt_meses_intervalo,
		c.ie_periodo,
		c.nr_seq_contrato,
		c.nr_seq_plano,
		c.ie_tipo_periodo,
		c.nr_sequencia,
		c.ie_qt_familia
	from	pls_limitacao		c,
		pls_tipo_limitacao	b,
		pls_limitacao_mat	a
	where	a.nr_seq_tipo_limitacao	= b.nr_sequencia
	and	c.nr_seq_tipo_limitacao	= b.nr_sequencia
	and	clock_timestamp() between coalesce(c.dt_inicio_vigencia,clock_timestamp()) and coalesce(c.dt_fim_vigencia,clock_timestamp())
	and	c.nr_sequencia		in	(SELECT	x.nr_sequencia
						from	pls_limitacao x
						where	x.nr_seq_contrato	= nr_seq_contrato_w
						and	((x.NR_SEQ_PLANO_CONTRATO = nr_seq_plano_w) or (coalesce(x.NR_SEQ_PLANO_CONTRATO::text, '') = ''))
						and	clock_timestamp() between coalesce(x.dt_inicio_vigencia,clock_timestamp()) and coalesce(x.dt_fim_vigencia,clock_timestamp())
						
union

						select	x.nr_sequencia
						from	pls_limitacao x
						where	x.nr_seq_intercambio	= nr_seq_intercambio_w
						and	clock_timestamp() between coalesce(x.dt_inicio_vigencia,clock_timestamp()) and coalesce(x.dt_fim_vigencia,clock_timestamp())
						
union

						select	x.nr_sequencia
						from	pls_limitacao x
						where	x.nr_seq_plano		= nr_seq_plano_w
						and	clock_timestamp() between coalesce(x.dt_inicio_vigencia,clock_timestamp()) and coalesce(x.dt_fim_vigencia,clock_timestamp())
						and	not exists (select	1
							from	pls_limitacao x
							where	x.nr_seq_contrato	= nr_seq_contrato_w
							and	clock_timestamp() between coalesce(x.dt_inicio_vigencia,clock_timestamp()) and coalesce(x.dt_fim_vigencia,clock_timestamp()))
						and	not exists (	select	1
									from	pls_limitacao z
									where	z.nr_seq_intercambio	= nr_seq_intercambio_w
									and	clock_timestamp() between coalesce(x.dt_inicio_vigencia,clock_timestamp()) and coalesce(x.dt_fim_vigencia,clock_timestamp()))
						
union

						/*Ajuste realizado pelo Alex August Schlote na OS 305595 */

						select	x.nr_sequencia
						from	pls_limitacao x
						where	x.nr_seq_plano		in (	select	t.nr_seq_plano
												from	pls_sca_vinculo	t
												where	t.nr_seq_segurado	= nr_seq_segurado_p
												and	(t.dt_liberacao IS NOT NULL AND t.dt_liberacao::text <> ''))
						and	not exists (	select	1
									from	pls_limitacao z
									where	z.nr_seq_contrato	= nr_seq_contrato_w
									and	clock_timestamp() between coalesce(z.dt_inicio_vigencia,clock_timestamp()) and coalesce(z.dt_fim_vigencia,clock_timestamp()))
						and	not exists (	select	1
									from	pls_limitacao z
									where	z.nr_seq_intercambio	= nr_seq_intercambio_w
									and	clock_timestamp() between coalesce(z.dt_inicio_vigencia,clock_timestamp()) and coalesce(z.dt_fim_vigencia,clock_timestamp()))
						and	not exists (	select	1
									from	pls_limitacao y
									where	y.nr_seq_plano		= nr_seq_plano_w
									and	clock_timestamp() between coalesce(y.dt_inicio_vigencia,clock_timestamp()) and coalesce(y.dt_fim_vigencia,clock_timestamp())))
	and	coalesce(a.cd_doenca_cid::text, '') = ''
	and	coalesce(a.ie_tipo_guia::text, '') = ''
	and (coalesce(a.nr_seq_material::text, '') = '' or a.nr_seq_material	= nr_seq_material_p)
	and	b.ie_situacao		= 'A'
	order by
		a.nr_seq_estrut_mat,
		a.nr_seq_material;

BEGIN

if (coalesce(nr_seq_guia_mat_p,0) > 0) then
	ie_tipo_consistencia_w	:= 'G';
elsif (coalesce(nr_seq_conta_mat_p,0) > 0) then
	ie_tipo_consistencia_w	:= 'C';
elsif (coalesce(nr_seq_requisicao_mat_p,0) > 0) then
	ie_tipo_consistencia_w	:= 'R';
end if;

begin
select	nr_seq_plano,
	nr_seq_contrato,
	nr_seq_intercambio
into STRICT	nr_seq_plano_w,
	nr_seq_contrato_w,
	nr_seq_intercambio_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;
exception
when others then
	nr_seq_plano_w		:= null;
	nr_seq_contrato_w	:= null;
	nr_seq_intercambio_w	:= NULL;
end;

if (ie_tipo_consistencia_w = 'G') then
	begin
	select	nr_seq_prestador,
		nr_seq_plano
	into STRICT	nr_seq_prestador_w,
		nr_seq_plano_w
	from	pls_guia_plano
	where	nr_sequencia = nr_seq_guia_p;
	exception
	when others then
		nr_seq_prestador_w	:= null;
		nr_seq_plano_w		:= null;
	end;
elsif (ie_tipo_consistencia_w = 'C') then
	select	nr_seq_conta
	into STRICT	nr_seq_conta_w
	from	pls_conta_mat
	where	nr_sequencia = nr_seq_conta_mat_p;

	begin
	select	a.nr_seq_prestador,
		a.ie_tipo_protocolo
	into STRICT	nr_seq_prestador_w,
		ie_tipo_protocolo_w
	from	pls_protocolo_conta a,
		pls_conta b
	where	a.nr_sequencia = b.nr_seq_protocolo
	and	b.nr_sequencia = nr_seq_conta_w;
	exception
	when others then
		nr_seq_prestador_w := null;
	end;

	if (ie_tipo_protocolo_w = 'R') then
		ie_tipo_consistencia_w := 'CR';
	end if;

elsif (ie_tipo_consistencia_w = 'R') then
	begin
	select	b.nr_seq_prestador,
		b.nr_sequencia,
		b.nr_seq_plano
	into STRICT	nr_seq_prestador_w,
		nr_seq_requisicao_w,
		nr_seq_plano_w
	from	pls_requisicao		b,
		pls_requisicao_mat	a
	where	a.nr_seq_requisicao	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_requisicao_mat_p;
	exception
	when others then
		nr_seq_prestador_w	:= null;
		nr_seq_plano_w		:= null;
	end;
end if;

open c01;
loop
fetch c01 into
	nr_seq_limitacao_mat_w,
	ie_liberado_w,
	nr_seq_estrut_mat_w,
	nr_seq_tipo_limitacao_w,
	qt_permitida_w,
	qt_meses_intervalo_w,
	ie_periodo_w,
	nr_seq_contrato_w,
	nr_seq_plano_w,
	ie_tipo_periodo_w,
	nr_seq_limitacao_w,
	ie_qt_familia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (coalesce(ie_qt_familia_w,'N')	= 'S') then
		select	nr_seq_titular
		into STRICT	nr_seq_titular_w
		from	pls_segurado
		where	nr_sequencia	= nr_seq_segurado_p
		and	ie_situacao_atend	= 'A'
		and	substr(pls_obter_dados_segurado(nr_sequencia,'CS'),1,10) = 'A';

		if (coalesce(nr_seq_titular_w::text, '') = '') then
			select	count(1)
			into STRICT	qt_pessoas_w
			from	pls_segurado
			where	nr_seq_titular	= nr_seq_segurado_p
			and	ie_situacao_atend	= 'A'
			and	substr(pls_obter_dados_segurado(nr_sequencia,'CS'),1,10) = 'A';

			-- Deve ser adicionado mais 1 para que seja considerado o titular.
			qt_pessoas_w 	:= qt_pessoas_w + 1;
		else
			select	count(1)
			into STRICT	qt_pessoas_w
			from	pls_segurado
			where	nr_seq_titular	= nr_seq_titular_w
			and	ie_situacao_atend	= 'A'
			and	substr(pls_obter_dados_segurado(nr_sequencia,'CS'),1,10) = 'A';

			-- Deve ser adicionado mais 1 para que seja considerado o titular.
			qt_pessoas_w 	:= qt_pessoas_w + 1;
		end if;

		qt_permitida_w	:=	qt_pessoas_w * qt_permitida_w;
	end if;

	begin
	if (ie_liberado_w	= 'S') then
		if (nr_seq_estrut_mat_w IS NOT NULL AND nr_seq_estrut_mat_w::text <> '') then
			ie_liberado_w	:= pls_obter_se_estruturas_mat(nr_seq_estrut_mat_w,nr_seq_material_p);
		end if;
	end if;
	end;
end loop;
close c01;

ie_origem_w	:= 'C';
if (nr_seq_plano_w IS NOT NULL AND nr_seq_plano_w::text <> '') then
	select	max(coalesce(ie_tipo_operacao,'B'))
	into STRICT	ie_tipo_operacao_w
	from	pls_plano
	where	nr_sequencia = nr_seq_plano_w;
	if (ie_tipo_operacao_w = 'A') then
		ie_origem_w	:= 'A';
	else
		ie_origem_w	:= 'P';
	end if;
end if;
/*
if	(ie_liberado_w = 'S') then
	select	substr(pls_obter_se_limitacao(nr_seq_segurado_p,nr_seq_tipo_limitacao_w,qt_permitida_w,
		qt_meses_intervalo_w,ie_periodo_w,qt_solicitada_p,
		null,nr_seq_guia_p,'Q',
		'M',ie_tipo_periodo_w),1,1)
	into	ie_limita_w
	from	dual;
	if	(ie_limita_w = 'S') then
		if 	(ie_tipo_consistencia_w = 'G') then
			pls_gravar_motivo_glosa('1423',null,null,nr_seq_guia_mat_p,'Material com limitação',
						nm_usuario_p,ie_origem_w,'CG',nr_seq_prestador_w,
						'(Regra ' || nr_seq_tipo_limitacao_w || ' -> ' || nr_seq_limitacao_mat_w || ')',null);
			update	pls_guia_plano_mat
			set	nr_seq_tipo_limitacao	= nr_seq_tipo_limitacao_w
			where	nr_sequencia		= nr_seq_guia_mat_p;
		elsif	(ie_tipo_consistencia_w = 'C') then
			pls_gravar_conta_glosa('1423',null, null,
						nr_seq_conta_mat_p,'N','Material com limitação',
						nm_usuario_p,'A','CC',
						nr_seq_prestador_w, cd_estabelecimento_p, '', null);
			update	pls_conta_mat
			set	nr_seq_tipo_limitacao	= nr_seq_tipo_limitacao_w
			where	nr_sequencia		= nr_seq_conta_mat_p;
		elsif	(ie_tipo_consistencia_w = 'R') then
			pls_gravar_requisicao_glosa('1423', null, null,
				nr_seq_requisicao_mat_p, 'Material com limitação', nm_usuario_p,
				nr_seq_prestador_w, cd_estabelecimento_p, null,
				'');

			update	pls_requisicao_mat
			set	nr_seq_tipo_limitacao	= nr_seq_tipo_limitacao_w
			where	nr_sequencia		= nr_seq_requisicao_mat_p;
		end if;
	end if;
end if;*/
if (ie_liberado_w = 'S') then
	if (ie_tipo_consistencia_w = 'G') then
		ie_limita_w	:= 	substr(pls_obter_se_limitacao(nr_seq_segurado_p, nr_seq_tipo_limitacao_w, qt_permitida_w,
						qt_meses_intervalo_w, ie_periodo_w, qt_solicitada_p,
						null, nr_seq_guia_p, 'Q',
						'M', ie_tipo_periodo_w,nr_seq_limitacao_w),1,1);

		if (ie_limita_w = 'S') then
			CALL pls_gravar_motivo_glosa('1423',null,null,nr_seq_guia_mat_p,'Material com limitação',
						nm_usuario_p,ie_origem_w,'CG',nr_seq_prestador_w,
						'(Regra ' || nr_seq_tipo_limitacao_w || ' -> ' || nr_seq_limitacao_mat_w || ')',null);
			update	pls_guia_plano_mat
			set	nr_seq_tipo_limitacao	= nr_seq_tipo_limitacao_w
			where	nr_sequencia		= nr_seq_guia_mat_p;
		end if;
	elsif (ie_tipo_consistencia_w = 'C') then

		ie_limita_w := pls_se_limitacao_conta_mat(	nr_seq_segurado_p, nr_seq_tipo_limitacao_w, qt_permitida_w, qt_meses_intervalo_w, ie_periodo_w, qt_solicitada_p, nr_seq_conta_mat_p, ie_limita_w, ie_tipo_periodo_w, nm_usuario_p, cd_estabelecimento_p);

		if (ie_limita_w = 'S') then
			CALL pls_gravar_conta_glosa('1423',null, null,
						nr_seq_conta_mat_p,'N','Material com limitação',
						nm_usuario_p,'A','CC',
						nr_seq_prestador_w, cd_estabelecimento_p, '', null);
			update	pls_conta_mat
			set	nr_seq_tipo_limitacao	= nr_seq_tipo_limitacao_w
			where	nr_sequencia		= nr_seq_conta_mat_p;
		end if;
	elsif (ie_tipo_consistencia_w = 'R') then
		ie_limita_w	:= substr(pls_obter_se_limitacao_req(nr_seq_segurado_p, nr_seq_tipo_limitacao_w, qt_permitida_w,
						qt_meses_intervalo_w, ie_periodo_w, qt_solicitada_p,
						null, nr_seq_requisicao_w, 'Q',
						'M',ie_tipo_periodo_w, nr_seq_limitacao_w),1,1);

		if (ie_limita_w = 'S') then
			CALL pls_gravar_requisicao_glosa('1423', null, null,
						nr_seq_requisicao_mat_p, 'Material com limitação', nm_usuario_p,
						nr_seq_prestador_w, cd_estabelecimento_p, null,
						'');

			update	pls_requisicao_mat
			set	nr_seq_tipo_limitacao	= nr_seq_tipo_limitacao_w
			where	nr_sequencia		= nr_seq_requisicao_mat_p;
		end if;

	elsif (ie_tipo_consistencia_w = 'CR') then

		ie_limita_w := pls_se_limitacao_conta_mat(	nr_seq_segurado_p, nr_seq_tipo_limitacao_w, qt_permitida_w, qt_meses_intervalo_w, ie_periodo_w, qt_solicitada_p, nr_seq_conta_mat_p, ie_limita_w, ie_tipo_periodo_w, nm_usuario_p, cd_estabelecimento_p);

		if (ie_limita_w = 'S') then
			CALL pls_gravar_conta_glosa('1423',null, null,
						nr_seq_conta_mat_p,'N','Material com limitação',
						nm_usuario_p,'A','CR',
						nr_seq_prestador_w, cd_estabelecimento_p, '', null);
			update	pls_conta_mat
			set	nr_seq_tipo_limitacao	= nr_seq_tipo_limitacao_w
			where	nr_sequencia		= nr_seq_conta_mat_p;
		end if;
	end if;
end if;

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consistir_limitacao_mat ( nr_seq_segurado_p bigint, nr_seq_guia_mat_p bigint, nr_seq_conta_mat_p bigint, nr_seq_requisicao_mat_p bigint, nr_seq_material_p bigint, qt_solicitada_p bigint, nr_seq_guia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

