SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_limit_depen_prop ( nr_seq_proposta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

			 
cd_pessoa_fisica_w		bigint;
ds_erro_w			varchar(4000);
ie_consiste_limit_dep_w		varchar(10);
nr_seq_parentesco_w		bigint;
dt_limite_dep_w			timestamp;
nr_contrato_w			bigint;
nr_seq_contrato_w		bigint;
dt_inicio_proposta_w		timestamp;
nm_beneficiario_w		varchar(255);
			
C01 CURSOR FOR 
	SELECT	cd_beneficiario, 
		nr_seq_parentesco 
	from	pls_proposta_beneficiario 
	where	nr_seq_proposta	= nr_seq_proposta_p 
	and	(nr_seq_parentesco IS NOT NULL AND nr_seq_parentesco::text <> '');


BEGIN 
 
ie_consiste_limit_dep_w	:= coalesce(obter_valor_param_usuario(1232, 83, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p), 'N');
 
nr_seq_contrato_w	:= null;
 
select	nr_seq_contrato, 
	dt_inicio_proposta 
into STRICT	nr_contrato_w, 
	dt_inicio_proposta_w 
from	pls_proposta_adesao 
where	nr_sequencia	= nr_seq_proposta_p;
 
if (nr_contrato_w IS NOT NULL AND nr_contrato_w::text <> '') then 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_contrato_w 
	from	pls_contrato 
	where	nr_contrato	= nr_contrato_w;
end if;
 
ds_erro_w	:= '';
 
if (ie_consiste_limit_dep_w = 'S') then 
	 
	open C01;
	loop 
	fetch C01 into	 
		cd_pessoa_fisica_w, 
		nr_seq_parentesco_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		if (length(ds_erro_w) > 230) then 
			exit;
		end if;
		 
		begin 
		dt_limite_dep_w		:= pls_obter_data_limi_pf_benef(cd_pessoa_fisica_w,nr_seq_contrato_w,null,nr_seq_parentesco_w);
		exception 
		when others then 
			dt_limite_dep_w	:= null;
		end;
 
		if (dt_limite_dep_w < dt_inicio_proposta_w) then 
			ds_erro_w	:= ds_erro_w|| wheb_mensagem_pck.get_texto(280205, 'CD_PESSOA_FISICA_P=' || substr(OBTER_NOME_PF(cd_pessoa_fisica_w),1,60));	
		end if;
		 
		end;
	end loop;
	close C01;
end if;
 
ds_erro_p	:= ds_erro_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
