CREATE OR REPLACE PROCEDURE pls_consistir_lote_pagamento ( nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_mensagem_p INOUT text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Consistir as informações do lote de pagamento
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
	OPS - Pagamentos de produção Médica
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_mensagem_w			varchar(255) := null;


BEGIN
/* Comentado pois diferente do que foi passado pelos clientes, existem situações onde deverão haver mais de um lote em uma mesma conta. OS 662764 - ebcabral -  27/11/2013
if	(nr_seq_lote_p is not null) then
	select	substr(decode(count(1),0,null,'Existem itens de contas que não estão no mesmo lote de pagamento'),1,255) ds_mensagem
	into	ds_mensagem_w
	from	pls_conta_medica_resumo	a
	where	((a.nr_seq_lote_pgto not in	(select	x.nr_seq_lote_pgto
						from	pls_conta_medica_resumo	x
						where	x.nr_seq_conta	= a.nr_seq_conta
						and	x.ie_tipo_item <> 'I'
						and	((x.nr_seq_conta_proc is not null) or (x.nr_seq_conta_mat is not null))
						and	((x.ie_situacao = 'A') or (x.ie_situacao is null))
						and	x.nr_seq_prestador_pgto = a.nr_seq_prestador_pgto
						and	x.nr_seq_lote_pgto	= nr_seq_lote_p)) or
		(a.nr_seq_lote_pgto is null))
	and	exists	(select	1
			from	pls_conta_medica_resumo	x
			where	x.nr_seq_conta	= a.nr_seq_conta
			and	x.nr_seq_lote_pgto is not null
			and	x.ie_tipo_item <> 'I'
			and	((x.nr_seq_conta_proc is not null) or (x.nr_seq_conta_mat is not null))
			and	((x.ie_situacao = 'A') or (x.ie_situacao is null))
			and	x.nr_seq_prestador_pgto = a.nr_seq_prestador_pgto
			and	x.nr_seq_lote_pgto	= nr_seq_lote_p)
	and	((a.ie_situacao = 'A') or (a.ie_situacao is null))
	and	a.ie_tipo_item <> 'I'
	and	((a.nr_seq_conta_proc is not null) or (a.nr_seq_conta_mat is not null));
end if;*/
ds_mensagem_p	:= substr(ds_mensagem_w,1,255);

end;
$body$
