CREATE OR REPLACE PROCEDURE pls_consistir_lote_reemb_recup ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_contrato_w			pls_lote_reemb_recuperacao.nr_contrato%type;
nr_contrato_principal_w		pls_lote_reemb_recuperacao.nr_contrato_principal%type;
nr_seq_contrato_w		pls_contrato.nr_sequencia%type;
nr_seq_contrato_principal_w	pls_contrato.nr_sequencia%type;


BEGIN

-- Tratamento do NR_SEQ_CONTRATO
select	max(nr_contrato)
into STRICT	nr_contrato_w
from	pls_lote_reemb_recuperacao
where	nr_sequencia = nr_sequencia_p;

if (coalesce(nr_contrato_w,0) <> 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_contrato_w
	from	pls_contrato
	where	nr_contrato = nr_contrato_w;

	if (coalesce(nr_seq_contrato_w,0) <> 0) then
		update	pls_lote_reemb_recuperacao
		set	nr_seq_contrato	= nr_seq_contrato_w
		where	nr_sequencia	= nr_sequencia_p;
	end if;
else
	update	pls_lote_reemb_recuperacao
	set	nr_seq_contrato	 = NULL
	where	nr_sequencia	= nr_sequencia_p;
end if;

-- Tratamento do NR_SEQ_CONTRATO_PRINCIPAL
select	max(nr_contrato_principal)
into STRICT	nr_contrato_principal_w
from	pls_lote_reemb_recuperacao
where	nr_sequencia = nr_sequencia_p;

if (coalesce(nr_contrato_principal_w,0) <> 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_contrato_principal_w
	from	pls_contrato
	where	nr_contrato = nr_contrato_principal_w;

	if (coalesce(nr_seq_contrato_principal_w,0) <> 0) then
		update	pls_lote_reemb_recuperacao
		set	nr_seq_contrato_principal	= nr_seq_contrato_principal_w
		where	nr_sequencia			= nr_sequencia_p;
	end if;
else
	update	pls_lote_reemb_recuperacao
	set	nr_seq_contrato_principal	 = NULL
	where	nr_sequencia			= nr_sequencia_p;
end if;

commit;

end;
