CREATE OR REPLACE PROCEDURE pls_consistir_medico_js ( cd_medico_p text, nm_usuario_p text, nr_seq_prestador_p bigint, nr_crm_exec_p text, ds_retorno_p INOUT text, ds_mensagem_p INOUT text) AS $body$
DECLARE


ds_retorno_w	varchar(255) := '';
ds_mensagem_w	varchar(255) := '';
qt_prestador_w	bigint;
qt_pessoa_w	bigint;


BEGIN

ds_retorno_w := pls_consistir_medico(cd_medico_p, nm_usuario_p, ds_retorno_w);

select	count(*)
into STRICT	qt_prestador_w
from	pls_prestador
where	(cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '')
and	nr_sequencia = nr_seq_prestador_p;

if (qt_prestador_w = 0) then

	select	count(cd_pessoa_fisica)
	into STRICT	qt_pessoa_w
	from	medico
	where	nr_crm = nr_crm_exec_p
	and	ie_situacao <> 'I';

	if (qt_pessoa_w > 1) then
		ds_mensagem_w := obter_texto_dic_objeto(125857,wheb_usuario_pck.get_nr_seq_idioma,null);
	end if;

end if;

ds_retorno_p := ds_retorno_w;
ds_mensagem_p := ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
