CREATE OR REPLACE PROCEDURE pls_consistir_medico ( cd_medico_p text, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE

ie_situacao_w			varchar(10);
qt_medico_w			bigint;

BEGIN
if (coalesce(cd_medico_p::text, '') = '') then
	ds_retorno_p	:= 'Favor informar um profissional!';
else
	select	count(*)
	into STRICT	qt_medico_w
	from	medico
	where	cd_pessoa_fisica	= cd_medico_p;

	if (qt_medico_w	> 0) then
		select	max(ie_situacao)
		into STRICT	ie_situacao_w
		from	medico
		where	cd_pessoa_fisica	= cd_medico_p;
		if (ie_situacao_w	= 'I') then
			ds_retorno_p	:= 'O profissional informado esta inativo, favor verificar!';
		end if;
	else
		ds_retorno_p	:= 'Profissional informado inv√°lido, favor verificar!';
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
