SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_parenteco_plano ( nr_seq_plano_p bigint, nr_seq_segurado_p bigint, ie_tipo_consistencia_p text, nm_usuario_p text) AS $body$
DECLARE

 
qt_regras_parentesco_w	bigint;
qt_registros_w		bigint;
nr_seq_parentesco_w	bigint;
nr_seq_contrato_w	bigint;
ds_plano_w		varchar(255);
nm_segurado_w		varchar(255);
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;
qt_idade_w		bigint;


BEGIN 
 
select	count(*) 
into STRICT	qt_regras_parentesco_w 
from	pls_plano_parentesco 
where	nr_seq_plano	= nr_seq_plano_p 
and	ie_situacao	= 'A';
 
if (qt_regras_parentesco_w > 0) then 
	if (ie_tipo_consistencia_p = 'C') then 
		select	nr_seq_parentesco, 
			nr_seq_contrato, 
			obter_idade_pf(cd_pessoa_fisica,clock_timestamp(),'A') qt_idade 
		into STRICT	nr_seq_parentesco_w, 
			nr_seq_contrato_w, 
			qt_idade_w 
		from	pls_segurado 
		where	nr_sequencia	= nr_seq_segurado_p;
		 
		select	count(*) 
		into STRICT	qt_registros_w 
		from	pls_plano_parentesco 
		where	nr_seq_parentesco	= nr_seq_parentesco_w 
		and	((nr_seq_plano		= nr_seq_plano_p) or (nr_seq_contrato = nr_seq_contrato_w)) 
		and	qt_idade_w between coalesce(qt_idade_min,0) and coalesce(qt_idade_max,999) 
		and	ie_situacao	= 'A';
		 
		if (qt_registros_w	= 0) then 
			ds_plano_w := substr(pls_obter_dados_produto(nr_seq_plano_p,'N'),1,255);
			nm_segurado_w := substr(pls_obter_dados_segurado(nr_seq_segurado_p,'N'),1,255);
		 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(267423, 
								'DS_PLANO_W=' || ds_plano_w||';'|| 
								'NM_SEGURADO_W=' || nm_segurado_w);
		end if;
	elsif (ie_tipo_consistencia_p	= 'P') then 
		select	nr_seq_parentesco, 
			cd_beneficiario, 
			obter_idade_pf(cd_beneficiario,clock_timestamp(),'A') qt_idade 
		into STRICT	nr_seq_parentesco_w, 
			cd_pessoa_fisica_w, 
			qt_idade_w 
		from	pls_proposta_beneficiario 
		where	nr_sequencia	= nr_seq_segurado_p;
		 
		select	count(*) 
		into STRICT	qt_registros_w 
		from	pls_plano_parentesco 
		where	nr_seq_plano		= nr_seq_plano_p 
		and	nr_seq_parentesco	= nr_seq_parentesco_w 
		and	qt_idade_w between coalesce(qt_idade_min,0) and coalesce(qt_idade_max,999)		 
		and	ie_situacao	= 'A';
		 
		if (qt_registros_w	= 0) then 
			ds_plano_w := substr(pls_obter_dados_produto(nr_seq_plano_p,'N'),1,255);
			nm_segurado_w := substr(obter_nome_pf(cd_pessoa_fisica_w),1,255);
			 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(267423, 
								'DS_PLANO_W=' || ds_plano_w||';'|| 
								'NM_SEGURADO_W=' || nm_segurado_w);
		end if;
	end if;
end if;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
