CREATE OR REPLACE PROCEDURE pls_consistir_pessoa_contrato ( nr_seq_segurado_p bigint, nr_seq_contrato_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text, nr_seq_pagador_p bigint, ie_opcao_p text, nm_usuario_p text, ds_mensagem_p INOUT text) AS $body$
DECLARE


/*ie_opcao_p
	P - Pagador
	B- benefici치rio*/
qt_registro_w			varchar(10);


BEGIN

if (ie_opcao_p	= 'P') then

	select	count(*)
	into STRICT	qt_registro_w
	from	pls_contrato_pagador
	where	nr_seq_contrato		= nr_seq_contrato_p
	and (cd_cgc			= coalesce(cd_cgc_p,'')
	or	cd_pessoa_fisica	= coalesce(cd_pessoa_fisica_p,''))
	and 	nr_sequencia 		<> nr_seq_pagador_p;
	if (qt_registro_w	> 0) then
		ds_mensagem_p	:=	'Pagador j치 cadastrado, favor verificar!';
	end if;

elsif (ie_opcao_p	= 'B') then
	select	count(*)
	into STRICT	qt_registro_w
	from	pls_segurado
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	nr_seq_contrato		= nr_seq_contrato_p
	and	nr_sequencia		<> nr_seq_segurado_p
	and	coalesce(dt_rescisao::text, '') = '';

	if (qt_registro_w	> 0) then
		ds_mensagem_p	:=	'Benefici치rio j치 cadastrado, favor verificar!';
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
