SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_plano_sca ( nr_seq_segurado_p bigint, nr_seq_plano_p bigint, nr_seq_tabela_p bigint, nr_seq_pagador_p bigint, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

 
qt_retorno_plano_w 		bigint;
ie_titularidade_seg_w		varchar(2);
ie_grau_dependencia_w		varchar(2);
ds_dependencia_w		varchar(40);
ds_plano_w			varchar(80);
dt_fim_vigencia_w		timestamp;
nr_seq_pagador_w		bigint;
nr_seq_restringir_sca_w		bigint;
qt_plano_adic_w			bigint;
nr_seq_produto_w		bigint;

C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from 	pls_restringir_plano_sca 
	where 	nr_seq_plano = nr_seq_produto_w 
	and 	ie_situacao = 'A' 
	and 	clock_timestamp() between dt_inicio_vigencia_ref and dt_fim_vigencia_ref 
	order by 1;


BEGIN 
 
select	count(*) 
into STRICT	qt_retorno_plano_w 
from	pls_plano 
where	nr_sequencia 		= nr_seq_plano_p 
and	ie_tipo_operacao	<> 'A';
 
if (coalesce(qt_retorno_plano_w,0) > 0) then 
	ds_erro_p := wheb_mensagem_pck.get_texto(280397, 'NR_SEQ_PLANO_P=' || nr_seq_plano_p);
end if;
 
select	coalesce(ie_grau_dependencia,'A'), 
	ds_plano 
into STRICT	ie_grau_dependencia_w, 
	ds_plano_w 
from	pls_plano 
where	nr_sequencia		= nr_seq_plano_p 
and	cd_estabelecimento	= cd_estabelecimento_p;
 
select	CASE WHEN ie_grau_dependencia_w='T' THEN 'titular'  ELSE 'dependente' END  
into STRICT	ds_dependencia_w
;
 
select	pls_obter_dados_segurado(nr_seq_segurado_p, 'NRP') 
into STRICT	nr_seq_produto_w
;	
 
if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then 
	select	CASE WHEN coalesce(nr_seq_titular::text, '') = '' THEN 'T'  ELSE 'D' END , 
		nr_seq_pagador 
	into STRICT	ie_titularidade_seg_w, 
		nr_seq_pagador_w 
	from	pls_segurado 
	where	nr_sequencia		= nr_seq_segurado_p;
 
	if	(ie_grau_dependencia_w <> 'A' AND ie_grau_dependencia_w <> ie_titularidade_seg_w) then 
		ds_erro_p := wheb_mensagem_pck.get_texto(280400, 'NR_SEQ_PLANO_P=' || nr_seq_plano_p || 
								';DS_PLANO_P=' || ds_plano_w || 
								';DS_DEPENDENCIA_P=' || ds_dependencia_w);
	end if;
	 
	if (nr_seq_pagador_p IS NOT NULL AND nr_seq_pagador_p::text <> '') then 
		if (nr_seq_pagador_w = nr_seq_pagador_p) then 
			ds_erro_p := wheb_mensagem_pck.get_texto(280428);
		end if;
	end if;
end if;	
 
if (nr_seq_tabela_p IS NOT NULL AND nr_seq_tabela_p::text <> '') then 
	select	max(dt_fim_vigencia) 
	into STRICT	dt_fim_vigencia_w 
	from	pls_tabela_preco 
	where	nr_sequencia	= nr_seq_tabela_p;
 
	if (coalesce(dt_fim_vigencia_w,clock_timestamp()) < clock_timestamp()) then 
		ds_erro_p	:= wheb_mensagem_pck.get_texto(280432);
	end if;
end if;	
 
open C01;
loop 
fetch C01 into 
	nr_seq_restringir_sca_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	select	count(*) 
	into STRICT	qt_plano_adic_w 
	from 	pls_restringir_sca_item 
	where 	nr_seq_regra = nr_seq_restringir_sca_w 
	and 	nr_seq_plano_adic = nr_seq_plano_p;	
 
	if (qt_plano_adic_w > 0) then 
		ds_erro_p := ds_erro_p || wheb_mensagem_pck.get_texto(472075,'NR_SEQ_SCA='||nr_seq_plano_p||';NR_SEQ_PLANO='||nr_seq_produto_w);
	end if;
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consistir_plano_sca ( nr_seq_segurado_p bigint, nr_seq_plano_p bigint, nr_seq_tabela_p bigint, nr_seq_pagador_p bigint, cd_estabelecimento_p bigint, ds_erro_p INOUT text) FROM PUBLIC;

