CREATE OR REPLACE PROCEDURE pls_consistir_pos_estab_fatura ( nr_seq_contrato_p bigint, nr_seq_regra_pos_estab_p bigint, ie_tipo_guia_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


qt_regra_pos_estab_fatura_w	bigint;
ds_tipo_guia_w			varchar(255);


BEGIN

select	count(*)
into STRICT	qt_regra_pos_estab_fatura_w
from	pls_regra_pos_estab_fatura
where	nr_seq_contrato	= nr_seq_contrato_p
and	nr_sequencia	<> nr_seq_regra_pos_estab_p
and	ie_tipo_guia	= ie_tipo_guia_p
and	ie_situacao	= 'A';

if (qt_regra_pos_estab_fatura_w > 0) then
	select	substr(obter_valor_dominio(1746, ie_tipo_guia_p),1,255)
	into STRICT	ds_tipo_guia_w
	;

	ds_erro_p	:= wheb_mensagem_pck.get_texto(280922, 'DS_TIPO_GUIA_P=' || ds_tipo_guia_w);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
