CREATE OR REPLACE PROCEDURE pls_consistir_prestador_mat ( nr_seq_regra_p bigint, nr_seq_prestador_p bigint, nr_seq_estrutura_mat_p bigint, nr_seq_material_p bigint, nr_seq_prest_fornec_p bigint, dt_inicio_vigencia_p text, dt_fim_vigencia_p text, nm_usuario_p text, nr_seq_fornec_mat_p bigint, nr_seq_grupo_material_p bigint, nr_seq_tipo_prestador_p bigint) AS $body$
DECLARE


nr_seq_regra_w		bigint;
dt_inicio_vigencia_w	timestamp;
dt_fim_vigencia_w	timestamp;


BEGIN
if (dt_inicio_vigencia_p IS NOT NULL AND dt_inicio_vigencia_p::text <> '') then
	dt_inicio_vigencia_w		:= to_date(dt_inicio_vigencia_p);
end if;

if (dt_inicio_vigencia_p IS NOT NULL AND dt_inicio_vigencia_p::text <> '') then
	dt_fim_vigencia_w		:= to_date(dt_fim_vigencia_p);
end if;

select	max(a.nr_sequencia)
into STRICT	nr_seq_regra_w
from	pls_prestador_mat a
where 	a.nr_seq_prestador		= nr_seq_prestador_p
and	a.nr_sequencia			<> nr_seq_regra_p
and	coalesce(a.nr_seq_estrutura_mat,0)	= coalesce(nr_seq_estrutura_mat_p,0)
and	coalesce(a.nr_seq_material,0)	= coalesce(nr_seq_material_p,0)
and	coalesce(a.nr_seq_prest_fornec,0)	= coalesce(nr_seq_prest_fornec_p,0)
and	coalesce(a.nr_seq_fornec_mat,0)	= coalesce(nr_seq_fornec_mat_p,0)
and	coalesce(a.nr_seq_grupo_material,0)	= coalesce(nr_seq_grupo_material_p,0)
and	coalesce(a.nr_seq_tipo_prestador,0)	= coalesce(nr_seq_tipo_prestador_p,0)
and	(((coalesce(dt_inicio_vigencia,clock_timestamp()) between coalesce(dt_inicio_vigencia_w,clock_timestamp()) and coalesce(dt_fim_vigencia_w,clock_timestamp())) or (coalesce(dt_fim_vigencia,clock_timestamp()) between coalesce(dt_inicio_vigencia_w,clock_timestamp()) and coalesce(dt_fim_vigencia_w,clock_timestamp()))) or
	((coalesce(dt_inicio_vigencia,clock_timestamp()) between coalesce(dt_inicio_vigencia_w,clock_timestamp()) and coalesce(dt_fim_vigencia_w,clock_timestamp())) and (coalesce(dt_fim_vigencia,clock_timestamp()) between coalesce(dt_inicio_vigencia_w,clock_timestamp()) and coalesce(dt_fim_vigencia_w,clock_timestamp()))));

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(238973, 'NR_SEQ_REGRA=' || nr_seq_regra_w);
end if;

end;
$body$
