CREATE OR REPLACE PROCEDURE pls_consistir_prestador_proc ( nr_seq_regra_p bigint, nr_seq_prestador_p bigint, cd_area_procedimento_p bigint, cd_especialidade_p bigint, cd_grupo_proc_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, dt_inicio_vigencia_p text, dt_fim_vigencia_p text, nr_seq_grupo_serv_p bigint, nr_seq_prestador_exec_p bigint, ie_tipo_guia_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_regra_w			bigint;
dt_inicio_vigencia_w		timestamp;
dt_fim_vigencia_w		timestamp;


BEGIN
dt_inicio_vigencia_w		:= to_date(dt_inicio_vigencia_p);
dt_fim_vigencia_w		:= to_date(dt_fim_vigencia_p);

select	max(a.nr_sequencia)
into STRICT	nr_seq_regra_w
from	pls_prestador_proc a
where	a.nr_seq_prestador		= nr_seq_prestador_p
and	a.nr_seq_prestador_exec		= nr_seq_prestador_exec_p
and	a.nr_sequencia			<> nr_seq_regra_p
and	coalesce(a.cd_area_procedimento,0)	= coalesce(cd_area_procedimento_p,0)
and	coalesce(a.cd_especialidade_proc,0)	= coalesce(cd_especialidade_p,0)
and	coalesce(a.cd_grupo_proc,0)		= coalesce(cd_grupo_proc_p,0)
and	coalesce(a.cd_procedimento,0)	= coalesce(cd_procedimento_p,0)
and	coalesce(a.ie_origem_proced,0)	= coalesce(ie_origem_proced_p,0)
and	coalesce(nr_seq_grupo_serv,0)	= coalesce(nr_seq_grupo_serv_p,0)
and	coalesce(ie_tipo_guia, '0')		= coalesce(ie_tipo_guia_p, '0')
and	(((coalesce(dt_inicio_vigencia,clock_timestamp()) between trunc(coalesce(dt_inicio_vigencia_w,clock_timestamp()),'dd') and trunc(coalesce(dt_fim_vigencia_w,clock_timestamp()),'dd')) or (coalesce(dt_fim_vigencia,clock_timestamp()) between trunc(coalesce(dt_inicio_vigencia_w,clock_timestamp()),'dd') and trunc(coalesce(dt_fim_vigencia_w,clock_timestamp()),'dd'))) or
	((coalesce(dt_inicio_vigencia,clock_timestamp()) between trunc(coalesce(dt_inicio_vigencia_w,clock_timestamp()),'dd') and trunc(coalesce(dt_fim_vigencia_w,clock_timestamp()),'dd')) and (coalesce(dt_fim_vigencia,clock_timestamp()) between trunc(coalesce(dt_inicio_vigencia_w,clock_timestamp()),'dd') and trunc(coalesce(dt_fim_vigencia_w,clock_timestamp()),'dd'))));

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(194366);
end if;

end;
$body$
