SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_prest_ressarc ( nm_prestador_p text, cd_cgc_p text, cd_cnes_p text, nr_seq_proc_prest_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


qt_registros_w			bigint;
qt_reg_cgc_w			bigint;
cd_cnes_w			varchar(255);
ds_retorno_w			varchar(255)	:= '';


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	pls_processo_prestador
where	upper(nm_prestador)	= upper(nm_prestador_p);

if (qt_registros_w > 0) then
	ds_retorno_w	:= ds_retorno_w || 'Já existe um registro com o nome do prestador informado,' || chr(13) || chr(10);
end if;

select	count(*)
into STRICT	qt_reg_cgc_w
from	pls_processo_prestador
where	cd_cgc	= cd_cgc_p;

if (qt_reg_cgc_w > 0) then
	ds_retorno_w	:= ds_retorno_w || 'Já existe um registro com o CNPJ do prestador informado. ' || chr(13) || chr(10);
end if;

select	max(coalesce(coalesce(a.cd_cnes, b.cd_cnes), '0'))
into STRICT	cd_cnes_w
FROM pessoa_juridica a
LEFT OUTER JOIN pls_processo_prestador b ON (a.cd_cgc = b.cd_cgc)
WHERE b.cd_cnes = cd_cnes_p and ((b.nr_sequencia <> nr_seq_proc_prest_p) or (coalesce(nr_seq_proc_prest_p, 0) = 0));

if (cd_cnes_w <> '0') then
	ds_retorno_w	:= ds_retorno_w || 'Já existe um registro com o CNES do prestador informado. ' || chr(13) || chr(10);
end if;

ds_retorno_p	:= substr(ds_retorno_w,1,length(ds_retorno_w)-2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
