SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_prop_titular ( nr_seq_proposta_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ie_titular_pf_w		varchar(1);
ds_erro_w		varchar(250);
qt_titular_w		bigint;
qt_proposta_pf_w	bigint;
nr_contrato_w		bigint;
nr_seq_contrato_w	bigint;
qt_contrato_pf_w	bigint := 0;
ie_tipo_proposta_w	smallint;


BEGIN

ie_titular_pf_w	:= coalesce(obter_valor_param_usuario(1232, 41, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p), 'N');

select	ie_tipo_proposta /* Domínio 2460 */
into STRICT	ie_tipo_proposta_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_p;

if (ie_tipo_proposta_w in (9,5)) then
	goto final;
end if;

select	count(*)
into STRICT	qt_proposta_pf_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_p
and	coalesce(cd_cgc_estipulante::text, '') = '';

select	nr_seq_contrato
into STRICT	nr_contrato_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_p;

/*aaschlote 13/05/2011 OS - 319421*/

if (nr_contrato_w IS NOT NULL AND nr_contrato_w::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_contrato_w
	from	pls_contrato
	where	nr_contrato	= nr_contrato_w;

	if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then
		select	count(*)
		into STRICT	qt_contrato_pf_w
		from	pls_segurado
		where	coalesce(nr_seq_titular::text, '') = ''
		and	coalesce(dt_rescisao::text, '') = ''
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and	nr_seq_contrato	= nr_seq_contrato_w;
	end if;
end if;

select	count(*)
into STRICT	qt_titular_w
from	pls_proposta_beneficiario
where	coalesce(nr_seq_titular::text, '') = ''
and	coalesce(nr_seq_titular_contrato::text, '') = ''
and	coalesce(dt_cancelamento::text, '') = ''
and	nr_seq_proposta	= nr_seq_proposta_p;

--aaschlote - Verificar os titulares do contrato também
if (qt_titular_w > 0) then
	qt_titular_w	:= qt_titular_w + coalesce(qt_contrato_pf_w,0);
end if;

if (qt_titular_w > 1) and (ie_titular_pf_w = 'S') and (qt_proposta_pf_w > 0) then
	ds_erro_w := ds_erro_w || wheb_mensagem_pck.get_texto(281038);
end if;


if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
	ds_erro_p	:= substr(ds_erro_w,1,255);
end if;

<<final>>
ds_erro_p	:= ds_erro_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
