CREATE OR REPLACE PROCEDURE pls_consistir_reaj_copartic (nr_seq_contrato_p bigint, nr_seq_regra_copartic_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


qt_regra_w	bigint;


BEGIN


select	count(*)
into STRICT	qt_regra_w
from	pls_tipo_coparticipacao a
where	a.ie_situacao = 'A'
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.nr_sequencia		= nr_seq_regra_copartic_p
and	exists (SELECT	1
		from	pls_regra_coparticipacao x
		where	x.nr_seq_tipo_coparticipacao	= a.nr_sequencia
		and	x.nr_seq_contrato		= nr_seq_contrato_p
		
union

		SELECT	1
		from	pls_contrato		y,
			pls_contrato_plano	w,
			pls_plano		z,
			pls_regra_coparticipacao q
		where	y.nr_sequencia		= nr_seq_contrato_p
		and	w.nr_seq_contrato	= y.nr_sequencia
		and	w.nr_seq_plano		= z.nr_sequencia
		and	q.nr_seq_plano		= z.nr_sequencia
		and	q.nr_seq_tipo_coparticipacao = a.nr_sequencia);

if (qt_regra_w = 0) then
	ds_erro_p	:= wheb_mensagem_pck.get_texto(280076);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
