CREATE OR REPLACE PROCEDURE pls_consistir_regra_alt_pag ( nr_seq_contrato_p pls_contrato.nr_sequencia%type, ie_manter_pagador_contas_p INOUT text) AS $body$
DECLARE


ie_acao_regra_w		pls_regra_alt_pag_contas.ie_acao_regra%type;
ie_tipo_estipulante_w	varchar(2);

C01 CURSOR FOR
	SELECT	ie_acao_regra
	from	pls_regra_alt_pag_contas a
	where	((a.ie_tipo_estipulante = ie_tipo_estipulante_w) or (a.ie_tipo_estipulante = 'A'))
	order by a.ie_tipo_estipulante;


BEGIN

select	CASE WHEN coalesce(max(cd_cgc_estipulante)::text, '') = '' THEN 'PF'  ELSE 'PJ' END
into STRICT	ie_tipo_estipulante_w
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_p;

open C01;
loop
fetch C01 into
	ie_acao_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

ie_manter_pagador_contas_p	:= coalesce(ie_acao_regra_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
