CREATE OR REPLACE PROCEDURE pls_consistir_regra_desf_susp ( nr_seq_desf_susp_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_contrato_w			bigint;
nr_seq_contrato_w		bigint;


BEGIN

select	max(nr_contrato)
into STRICT	nr_contrato_w
from	pls_regra_desfazer_susp
where	nr_sequencia	= nr_seq_desf_susp_p;

if (coalesce(nr_contrato_w,0) <> 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_contrato_w
	from	pls_contrato
	where	nr_contrato	= nr_contrato_w;

	if (coalesce(nr_seq_contrato_w,0) <> 0) then
		update	pls_regra_desfazer_susp
		set	nr_seq_contrato	= nr_seq_contrato_w,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_seq_desf_susp_p;
	end if;
else
	update	pls_regra_desfazer_susp
	set	nr_seq_contrato	 = NULL,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_desf_susp_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
