SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_regra_parentesco ( nr_seq_regra_titular_p bigint, nr_seq_regra_tit_paren_p bigint, cd_estabelecimento_p bigint, ds_erro_p INOUT text, nm_usuario_p text) AS $body$
DECLARE


ie_permite_qt_paren_w	varchar(10);
qt_registros_w		bigint;
qt_registros_novos_w	bigint;
ds_erro_w		varchar(255);


BEGIN
ds_erro_w		:= '';
/*Verificar parâmetro[16]*/

ie_permite_qt_paren_w	:= coalesce(obter_valor_param_usuario(1209, 16, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p), 'N');

/*Se o parâmetro estiver liberado, então consiste a regra*/

if (ie_permite_qt_paren_w	= 'S') then
	/*Contar se o registro incluido ou editado existe na banco*/

	select	count(*)
	into STRICT	qt_registros_novos_w
	from	pls_regra_tit_parentesco
	where	nr_seq_regra_titular	= nr_seq_regra_titular_p
	and	nr_sequencia		= nr_seq_regra_tit_paren_p;

	/*Se não existir, então incrementa mais 1*/

	if (qt_registros_novos_w	= 0) then
		qt_registros_novos_w	:= 1;
	end if;

	/*Contar os registro da regra, defirentes do que incluido ou editado*/

	select	count(*)
	into STRICT	qt_registros_w
	from	pls_regra_tit_parentesco
	where	nr_seq_regra_titular	= nr_seq_regra_titular_p
	and	nr_sequencia		<> nr_seq_regra_tit_paren_p;

	/*Agrupa todos os valores em uma variavel*/

	qt_registros_w	:= qt_registros_w + qt_registros_novos_w;

	/*Caso for maior que 1 a regra, então lança a mensagem da consistência*/

	if (qt_registros_w	> 1) then
		ds_erro_w	:= wheb_mensagem_pck.get_texto(280532);
	end if;
end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
