CREATE OR REPLACE PROCEDURE pls_consistir_simulpreco_indiv ( nr_seq_simulacao_p bigint, nr_seq_simul_indiv_p bigint, nr_seq_plano_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


qt_registros_w		bigint;
ie_plano_diferentes_w	varchar(10);
ds_erro_w		varchar(4000)	:= null;


BEGIN

ie_plano_diferentes_w	:= coalesce(obter_valor_param_usuario(1233, 3, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p), 'S');

/*if	(nvl(nr_seq_plano_p,0) = 0) then
	ds_erro_w	:= wheb_mensagem_pck.get_texto(296141);
end if;*/
if (nr_seq_plano_p IS NOT NULL AND nr_seq_plano_p::text <> '') then
	begin
	select	count(1)
	into STRICT	qt_registros_w
	from	pls_simulpreco_individual
	where	nr_seq_simulacao	= nr_seq_simulacao_p
	and	nr_sequencia		<> nr_seq_simul_indiv_p;
	exception
	when others then
		qt_registros_w	:= 0;
	end;

	if (qt_registros_w	> 0) then
		select	count(1)
		into STRICT	qt_registros_w
		from	pls_simulpreco_individual
		where	nr_seq_simulacao	= nr_seq_simulacao_p
		and	nr_sequencia		<> nr_seq_simul_indiv_p
		and	nr_seq_produto		= nr_seq_plano_p;

		if (qt_registros_w	= 0) and (ie_plano_diferentes_w	= 'N') then
			ds_erro_w	:= wheb_mensagem_pck.get_texto(280089);
		end if;
	end if;
end if;

if (coalesce(ds_erro_w::text, '') = '') then
	ds_erro_w := pls_consistir_bloq_benef_canc(nr_seq_simulacao_p, cd_pessoa_fisica_p, cd_estabelecimento_p, ds_erro_w);
end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
