SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_simul_comercial ( nr_seq_item_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*ie_opcao_p
S - Solicitação de lead
P - Prospecção
*/
ie_consistir_simul_w	varchar(1);
qt_registros_w		bigint;
nr_seq_solicitacao_w	bigint;


BEGIN

ie_consistir_simul_w	:= coalesce(obter_valor_param_usuario(1237, 36, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p), 'N');

if (ie_consistir_simul_w	= 'A') then
	if (ie_opcao_p	= 'S') then
		select	count(*)
		into STRICT	qt_registros_w
		from	pls_simulacao_preco
		where	nr_seq_solicitacao	= nr_seq_item_p;

		if (qt_registros_w	= 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(266783); -- A solicitação de lead não possui simulaçao de preço. Parâmetro[36]
		end if;
	elsif (ie_opcao_p	= 'P') then

		select	max(nr_seq_solicitacao)
		into STRICT	nr_seq_solicitacao_w
		from	pls_comercial_cliente
		where	nr_sequencia	= nr_seq_item_p;

		select	count(*)
		into STRICT	qt_registros_w
		from	pls_simulacao_preco
		where	((nr_seq_cliente = nr_seq_item_p) or (nr_seq_solicitacao = nr_seq_solicitacao_w));

		if (qt_registros_w	= 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(266784); -- O prospect não possui simulaçao de preço. Parâmetro[36]
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
