SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_tabela_rn63 ( nr_seq_tabela_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ie_consistir_rn63_w		pls_faixa_etaria.ie_consistir_rn63%type;
ie_tabela_principal_w		varchar(1);
vl_primeira_faixa_w		pls_plano_preco.vl_preco_atual%type;
vl_ultima_faixa_w		pls_plano_preco.vl_preco_atual%type;
vl_setima_faixa_w		pls_plano_preco.vl_preco_atual%type;
ds_erro_w			varchar(255);


BEGIN

begin
select	coalesce(max(b.ie_consistir_rn63),'N'),
	CASE WHEN coalesce(max(nr_seq_principal)::text, '') = '' THEN 'N'  ELSE 'S' END
into STRICT	ie_consistir_rn63_w,
	ie_tabela_principal_w
from	pls_tabela_preco a,
	pls_faixa_etaria b
where	b.nr_sequencia	= a.nr_seq_faixa_etaria
and	a.nr_sequencia	= nr_seq_tabela_p;
exception
when others then
	ie_consistir_rn63_w	:= 'N';
	ie_tabela_principal_w	:= 'N';
end;

if (ie_consistir_rn63_w = 'S') then
	select	coalesce(max(vl_preco_atual),1) -- sideker: não pode ser 0 porque esse valor será dividido no IF  abaixo
	into STRICT	vl_primeira_faixa_w
	from	pls_plano_preco
	where	nr_seq_tabela = nr_seq_tabela_p
	and	qt_idade_inicial = 0;

	select	coalesce(max(vl_preco_atual),0)
	into STRICT	vl_ultima_faixa_w
	from	pls_plano_preco
	where	nr_seq_tabela = nr_seq_tabela_p
	and	qt_idade_inicial = 59;

	if	(vl_ultima_faixa_w > (vl_primeira_faixa_w * 6)) then
		ds_erro_w	:= wheb_mensagem_pck.get_texto(358549,null);
		-- Mensagem: O valor fixado para a última faixa etária não poderá ser superior a seis vezes o valor da primeira faixa etária
	end if;

	select	coalesce(sum(vl_preco_atual),1) -- sideker: não pode ser 0 porque esse valor será dividido no IF  abaixo
	into STRICT	vl_setima_faixa_w
	from	pls_plano_preco
	where	nr_seq_tabela = nr_seq_tabela_p
	and	qt_idade_inicial = 44;

	if (vl_ultima_faixa_w > 0) and (vl_setima_faixa_w > 0) and (vl_primeira_faixa_w > 0) then
		if	((vl_ultima_faixa_w / vl_setima_faixa_w) > (vl_setima_faixa_w / vl_primeira_faixa_w)) then
			ds_erro_w	:= wheb_mensagem_pck.get_texto(358550,null);
			-- Mensagem: A variação acumulada entre a sétima e a décima faixas não poderá ser superior à variação acumulada entre a primeira e a sétima faixas
		end if;
	end if;
end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consistir_tabela_rn63 ( nr_seq_tabela_p bigint, ds_erro_p INOUT text) FROM PUBLIC;

