SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_titular_benef ( nr_seq_contrato_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_titular_pf_w		varchar(1);
ds_erro_w		varchar(250);
qt_titular_w		bigint;
qt_contrato_pf_w	bigint;


BEGIN

ie_titular_pf_w	:= coalesce(obter_valor_param_usuario(1202, 48, Obter_Perfil_Ativo, nm_usuario_p, 0), 'N');

if (ie_titular_pf_w = 'S') then
	select	count(1)
	into STRICT	qt_contrato_pf_w
	from	pls_contrato
	where	nr_sequencia	= nr_seq_contrato_p
	and	coalesce(cd_cgc_estipulante::text, '') = '';

	if (qt_contrato_pf_w > 0) then
		select	count(1)
		into STRICT	qt_titular_w
		from	pls_segurado
		where	nr_seq_contrato	= nr_seq_contrato_p
		and	coalesce(nr_seq_titular::text, '') = ''
		and	coalesce(dt_rescisao::text, '') = '';

		if (qt_titular_w > 1) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(203130);
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
