SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consistir_usuario_eventual ( nr_seq_plano_p bigint, ie_tipo_inclusao_p text, nr_seq_congenere_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
ie_tipo_inclusao_p
	'C' = Operadora congênere
	'T' = Intercâmbio
*/
ie_tipo_operacao_w	varchar(3);
qt_produto_congenere_w	bigint;


BEGIN

if (nr_seq_plano_p IS NOT NULL AND nr_seq_plano_p::text <> '') then
	select	max(ie_tipo_operacao)
	into STRICT	ie_tipo_operacao_w
	from	pls_plano
	where	nr_sequencia	= nr_seq_plano_p;

	if (ie_tipo_operacao_w	<> ie_tipo_inclusao_p) then
		if (ie_tipo_inclusao_p = 'T') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(262305);
			/* Mensagem: Deve ser selecionado um produto de intercâmbio! Favor verificar. */

		elsif (ie_tipo_inclusao_p = 'C') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(262306);
			/* Mesagem: Deve ser selecionado um produto para operadora congênere! Favor verificar. */

		end if;
	end if;

	if (ie_tipo_inclusao_p = 'C') then
		select	count(*)
		into STRICT	qt_produto_congenere_w
		from	pls_plano
		where	nr_sequencia		= nr_seq_plano_p
		and	nr_seq_congenere	= nr_seq_congenere_p
		and	ie_tipo_operacao	= 'C';

		if (qt_produto_congenere_w = 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(262307);
			/* Mesagem: Produto não cadastrado para a operadora congênere selecionada! Favor verificar. */

		end if;
	end if;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(262308);
	/* Mesagem: Deve ser selecionado um produto! Favor verificar. */

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
