CREATE OR REPLACE PROCEDURE pls_consistir_vinculo_end_js ( nm_tabela_p text, cd_estabelecimento_p bigint, cd_perfil_p bigint, cd_setor_p bigint, cd_funcao_p bigint, nr_seq_visao_p bigint, cd_pessoa_fisica_p text, cd_pessoa_end_ref_p text, nm_usuario_p text) AS $body$
