and	((nr_seq_visao = nr_seq_visao_p) or (coalesce(nr_seq_visao::text, '') = ''))
and (ie_obrigatorio = 'S' or ie_readonly = 'S')
and	nm_atributo <> 'NR_SEQUENCIA';

if (qt_existe_w > 0) then
