CREATE OR REPLACE PROCEDURE pls_consisti_sca_cumpriu_ant ( nr_seq_segurado_p bigint, nr_seq_plano_p bigint, dt_inicio_vigencia_p timestamp, nm_usuario_p text, ie_inclui_sca_p INOUT text) AS $body$
DECLARE


nr_seq_segurado_ant_w		bigint;
nr_seq_classificacao_w		bigint;
nr_seq_plano_sca_ant_w		bigint;
dt_cobranca_sca_ant_w		timestamp;
ie_incluir_sca_w		varchar(10);


BEGIN

ie_incluir_sca_w	:= 'S';

select	nr_seq_segurado_ant
into STRICT	nr_seq_segurado_ant_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

if (nr_seq_segurado_ant_w IS NOT NULL AND nr_seq_segurado_ant_w::text <> '') then
	select	nr_seq_classificacao
	into STRICT	nr_seq_classificacao_w
	from	pls_plano
	where	nr_sequencia	= nr_seq_plano_p;

	if (nr_seq_classificacao_w IS NOT NULL AND nr_seq_classificacao_w::text <> '') then
		begin
		select	max(c.nr_sequencia)
		into STRICT	nr_seq_plano_sca_ant_w
		from	pls_plano		c,
			pls_sca_vinculo		b,
			pls_segurado		a
		where	b.nr_seq_plano		= c.nr_sequencia
		and	b.nr_seq_segurado	= a.nr_sequencia
		and	c.nr_seq_classificacao	= nr_seq_classificacao_w
		and	a.nr_sequencia		= nr_seq_segurado_ant_w;
		exception
		when others then
			nr_seq_plano_sca_ant_w	:= null;
		end;

		if (nr_seq_plano_sca_ant_w IS NOT NULL AND nr_seq_plano_sca_ant_w::text <> '') then
			dt_cobranca_sca_ant_w	:= pls_obter_data_cobranca_sca(nr_seq_plano_sca_ant_w,nr_seq_segurado_ant_w);

			if (dt_cobranca_sca_ant_w IS NOT NULL AND dt_cobranca_sca_ant_w::text <> '') then
				if (trunc(dt_cobranca_sca_ant_w,'dd') < trunc(dt_inicio_vigencia_p,'dd')) then
					ie_incluir_sca_w	:= 'N';
				end if;
			end if;
		end if;
	end if;
end if;

ie_inclui_sca_p	:= ie_incluir_sca_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
