SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consist_conta_mat_web ( nr_seq_conta_p bigint, nr_seq_prestador_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_tipo_conta_web_p text, ie_situacao_protocolo_p INOUT text) AS $body$
DECLARE


nr_seq_conta_mat_w		bigint;
dt_atendimento_w		timestamp;
dt_fim_atend_w			timestamp;
dt_inicio_atend_w		timestamp;
ie_origem_preco_w		smallint;
ie_tipo_despesa_w		varchar(2);
tx_reducao_acrescimo_w		double precision;
ds_material_w			varchar(255);
nr_seq_material_w		bigint;
ie_situacao_protocolo_w		varchar(3)	:= 'A';
ie_tipo_relacao_w		varchar(2);
ie_tipo_guia_w			varchar(2);
ie_tipo_segurado_w		varchar(3);
cd_guia_w			varchar(20);
nr_seq_segurado_w		bigint;
nr_seq_plano_w			bigint;
nr_seq_congenere_w		bigint;
nr_seq_guia_w 			bigint	:= 0;
nr_seq_prest_fornec_w		bigint;
ie_origem_ocorrencia_w		varchar(1);
ie_mat_inativo_w		varchar(1)	:= 'S';
ie_portal_web_w			varchar(3);
ie_status_teste_w		varchar(5);
ds_obs_data_w			pls_conta_glosa.ds_observacao%type;
qt_saldo_w			double precision;
qt_utilizada_w			double precision;
qt_autorizada_w			double precision;
qt_solicitada_w			double precision;
qt_lanc_glosa_w			double precision;
qt_utilizada_glosa_w		double precision;
nr_seq_prestador_exec_w		pls_conta.nr_seq_prestador_exec%type;
ie_liberar_item_autor_w		varchar(3);
ie_existe_regra_w		varchar(3);
nr_seq_regra_autor_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_material,
		dt_atendimento,
		dt_fim_atend,
		dt_inicio_atend,
		ie_origem_preco,
		ie_tipo_despesa,
		tx_reducao_acrescimo,
		pls_obter_desc_material(nr_seq_material)
	from	pls_conta_mat
	where	nr_seq_conta	= nr_seq_conta_p
	and	ie_status <> 'D';


BEGIN

/* Obter dados do prestador */

select	coalesce(max(ie_tipo_relacao),'')
into STRICT	ie_tipo_relacao_w
from	pls_prestador
where	nr_sequencia	= nr_seq_prestador_p;

select	coalesce(ie_tipo_guia,0),
	ie_tipo_segurado,
	coalesce(cd_guia_referencia,cd_guia),
	nr_seq_segurado,
	nr_seq_plano,
	nr_seq_congenere,
	nr_seq_guia
into STRICT	ie_tipo_guia_w,
	ie_tipo_segurado_w,
	cd_guia_w,
	nr_seq_segurado_w,
	nr_seq_plano_w,
	nr_seq_congenere_w,
	nr_seq_guia_w
from	pls_conta
where	nr_sequencia	= nr_seq_conta_p;


if (cd_guia_w IS NOT NULL AND cd_guia_w::text <> '') and (coalesce(nr_seq_guia_w::text, '') = '')then
	begin
		select	nr_sequencia
		into STRICT	nr_seq_guia_w
		from	pls_guia_plano
		where	cd_guia		= cd_guia_w
		and	nr_seq_segurado = nr_seq_segurado_w;
	exception
	when others then
		nr_seq_guia_w := 0;
	end;
end if;

open C01;
loop
fetch C01 into
	nr_seq_conta_mat_w,
	nr_seq_material_w,
	dt_atendimento_w,
	dt_fim_atend_w,
	dt_inicio_atend_w,
	ie_origem_preco_w,
	ie_tipo_despesa_w,
	tx_reducao_acrescimo_w,
	ds_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	CALL pls_tiss_consistir_material(nr_seq_conta_mat_w, 'CM', 'DC',
		null, null, '',
		nm_usuario_p, cd_estabelecimento_p);

	if (ie_tipo_despesa_w = '7') then
		CALL pls_tiss_consistir_opm(nr_seq_conta_mat_w, 'CM', 'DC',
			null, null, '',
			nm_usuario_p, cd_estabelecimento_p);
	end if;

	if (ie_tipo_despesa_w = '2') then
		CALL pls_tiss_consistir_medicamento(nr_seq_conta_mat_w, 'CM', 'DC',
			null, null, '',
			nm_usuario_p, cd_estabelecimento_p);
	end if;

	ie_mat_inativo_w := pls_obter_se_mat_inativo(nr_seq_material_w,dt_atendimento_w);

	begin
	select	ie_status
	into STRICT	ie_status_teste_w
	from	pls_conta_mat
	where	nr_sequencia = nr_seq_conta_mat_w;
	exception
	when others then
		ie_status_teste_w	:= 'X';
	end;

	if (ie_mat_inativo_w = 'N') then
		CALL pls_gravar_glosa_tiss('9920',
			'Material inativo ou fora de vigência. (Função: OPS - Cadastro de Materiais)', 'DC',
			nr_seq_conta_mat_w, 'CM', null,
			null, '', nm_usuario_p,
			cd_estabelecimento_p);
	end if;

	ds_obs_data_w := pls_valida_regra_data_conta(dt_atendimento_w);

	if (ds_obs_data_w IS NOT NULL AND ds_obs_data_w::text <> '') then
		CALL pls_gravar_conta_glosa('1701',nr_seq_conta_p,null,
					null,'N',ds_obs_data_w || '. Seq item: ' || nr_seq_conta_mat_w,
					nm_usuario_p,'A','DC',
					nr_seq_prestador_p, cd_estabelecimento_p, '', null);
	end if;

	CALL pls_tiss_consistir_autorizacao(nr_seq_conta_mat_w, 'CM', 'DC',
			nr_seq_prestador_p, null, '',
			nm_usuario_p, cd_estabelecimento_p,null);


	/*Consistir se o material esta habilitado para o prestador, glosa 1214 drquadros - O.S 657237*/

	CALL pls_consistir_mat_prestador( 	nr_seq_prestador_p , nr_seq_conta_mat_w, null,
					null, null, cd_estabelecimento_p, nm_usuario_p);

	if (nr_seq_guia_w > 0) then
		begin
			select	coalesce(nr_seq_prest_fornec,0)
			into STRICT	nr_seq_prest_fornec_w
			from	pls_guia_plano_mat
			where	nr_seq_guia	= nr_seq_guia_w
			and	nr_seq_material = nr_seq_material_w;
		exception
		when others then
			nr_seq_prest_fornec_w := 0;
		end;

		if (nr_seq_prest_fornec_w > 0) then
			update	pls_conta_mat
			set	nr_seq_prest_fornec	= nr_seq_prest_fornec_w
			where	nr_sequencia		= nr_seq_conta_mat_w;
		end if;
	end if;

	/*Conforme visto com Paulo Rosa tem que atualizar o valor do material igual a rotina de consistencia do proc*/

	/*
	Retirado esse tratamento pois é feita a valorização conforme regra
	pls_atualiza_valor_mat(	nr_seq_conta_mat_w,'N',nm_usuario_p,
				null ,nr_seq_conta_p);	*/
	if (ie_tipo_conta_web_p = 'D') then
		ie_origem_ocorrencia_w := 'P';
		ie_portal_web_w	:= 'DC';
	else
		ie_origem_ocorrencia_w := 'C';
		ie_portal_web_w	:= 'CG';
	end if;

	select 	coalesce(max(qt_saldo),0),
		coalesce(max(qt_utilizada),0),
		coalesce(max(qt_autorizada),0)
	into STRICT	qt_saldo_w,
		qt_utilizada_w,
		qt_autorizada_w
	from 	table(pls_conta_autor_pck.obter_dados(nr_seq_guia_w,'M', cd_estabelecimento_p, null, null, nr_seq_material_w));

	SELECT * FROM pls_consiste_mat_autor(	nr_seq_conta_p, nr_seq_conta_mat_w, nm_usuario_p, cd_estabelecimento_p, nr_seq_prestador_exec_w, ie_liberar_item_autor_w, ie_existe_regra_w, nr_seq_regra_autor_w) INTO STRICT ie_liberar_item_autor_w, ie_existe_regra_w, nr_seq_regra_autor_w;

	if (ie_liberar_item_autor_w = 'S') then

		if (nr_seq_guia_w <> 0) then

			if (qt_utilizada_w = 0) then

				qt_utilizada_w := qt_solicitada_w;
			end if;

			/*Só ira verificar se o material não estiver glosado*/

			if	(qt_saldo_w < 0 AND qt_utilizada_w > 0) or (qt_autorizada_w = 0) then

				if (ie_tipo_despesa_w in ('1', '2', '3')) then

					CALL pls_gravar_conta_glosa(	'2009',null,null,
								nr_seq_conta_mat_w, 'N', '[1] Quantidade de materiais utilizados excede a quantidade de materiais autorizados. '||
											'Autorizada: '||qt_autorizada_w||' Utilizada: '||qt_utilizada_w || '. Regra de autorização: ' || nr_seq_regra_autor_w,
								nm_usuario_p, 'A', 'DC',
								nr_seq_prestador_exec_w, cd_estabelecimento_p, '',
								null);

				elsif (ie_tipo_despesa_w = '7') then /* OPM */
					qt_utilizada_glosa_w := qt_utilizada_w - qt_autorizada_w;
					qt_lanc_glosa_w := pls_obter_qt_glosa_item(	null, null, nr_seq_material_w,
								   			nr_seq_guia_w, '2206');

					if (qt_lanc_glosa_w < qt_utilizada_glosa_w) then

						CALL pls_gravar_conta_glosa(	'2206',null,null,
									nr_seq_conta_mat_w,'N','[1] Quantidade de OPMs utilizados excede a quantidade autorizada. '||
												'Autorizada: '||qt_autorizada_w||' Utilizada: '||qt_utilizada_w || '. Regra de autorização: ' || nr_seq_regra_autor_w,
									nm_usuario_p, 'A', 'DC',
									nr_seq_prestador_exec_w, cd_estabelecimento_p, '',
									null);
					end if;
				end if;
			end if;
		else
			if (coalesce(qt_solicitada_w,0) > 0) then

				if (ie_tipo_despesa_w in ('1', '2', '3')) then
					CALL pls_gravar_conta_glosa('2009',null,null,
								nr_seq_conta_mat_w,'N','[2] Quantidade de materiais utilizados excede a quantidade de materiais autorizados. '||
								'Autorizada: 0 Utilizada: '||qt_solicitada_w || '. Regra de autorização: ' || nr_seq_regra_autor_w,
								nm_usuario_p,'A','DC',
								nr_seq_prestador_exec_w, cd_estabelecimento_p, '', null);

				elsif (ie_tipo_despesa_w = '7') then /* OPM */
					CALL pls_gravar_conta_glosa('2206',null,null,
								nr_seq_conta_mat_w,'N','[2] - OPM sem autorização válida. Regra de autorização: ' || nr_seq_regra_autor_w,
								nm_usuario_p,'A','DC',
								nr_seq_prestador_exec_w, cd_estabelecimento_p, '', null);
				end if;
			end if;
		end if;
	end if;

	CALL pls_gerar_ocorrencia(	nr_seq_segurado_w, null, null,
			nr_seq_conta_p, null, nr_seq_conta_mat_w,
			null, null, nr_seq_material_w,
			ie_tipo_guia_w, nr_seq_plano_w, 'C',
			null, null,nr_seq_prestador_p,
			null,'I','',
			ie_origem_ocorrencia_w,nm_usuario_p, cd_estabelecimento_p,
			nr_seq_congenere_w ,null, ie_tipo_conta_web_p,
			null, null);
	end;
end loop;
close C01;

update	pls_conta_mat
set	ie_status	= 'C'
where	nr_seq_conta 	= nr_seq_conta_p
and	ie_status 	not in ('D','M');

ie_situacao_protocolo_p	:= ie_situacao_protocolo_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consist_conta_mat_web ( nr_seq_conta_p bigint, nr_seq_prestador_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_tipo_conta_web_p text, ie_situacao_protocolo_p INOUT text) FROM PUBLIC;

