CREATE OR REPLACE PROCEDURE pls_consist_dupl_proc_diaria ( nr_seq_reg_diaria_p pls_regra_diaria.nr_sequencia%type, nr_seq_reg_diaria_proc_p pls_regra_diaria_proc.nr_sequencia%type, ie_origem_proced_p pls_regra_diaria_proc.ie_origem_proced%type, cd_procedimento_p pls_regra_diaria_proc.cd_procedimento%type, nm_usuario_p usuario.nm_usuario%type, ds_mensagem_p INOUT text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Não permitir que na mesma regra de diárias seja cadastrado o mesmo procedimento mais de uma vez
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_registros_w	integer	:= 0;
ds_sql_w	varchar(4000);


BEGIN

if (nr_seq_reg_diaria_p IS NOT NULL AND nr_seq_reg_diaria_p::text <> '') then
	ds_sql_w	:= 	' select	count(1) '||
				' from		pls_regra_diaria_proc '||
				' where		nr_seq_regra_diaria	= :nr_seq_reg_diaria_p '||
				' and		nr_sequencia		<> :nr_seq_reg_diaria_proc_p';

	if (ie_origem_proced_p IS NOT NULL AND ie_origem_proced_p::text <> '') and (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
		ds_sql_w	:= ds_sql_w||	' and ie_origem_proced	= :ie_origem_proced ' ||
						' and cd_procedimento	= :cd_procedimento ';

		execute	immediate ds_sql_w
		into STRICT	qt_registros_w
		using	nr_seq_reg_diaria_p, nr_seq_reg_diaria_proc_p, ie_origem_proced_p, cd_procedimento_p;
	end if;

	if (qt_registros_w	> 0) then
		ds_mensagem_p	:= wheb_mensagem_pck.get_texto(285541);
	end if;
end if;

end;
$body$
