SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_consist_gerac_mens_contab ( nr_seq_lote_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


qt_mes_competencia_w		bigint;
dt_mesano_referencia_w		timestamp;
dt_contabilizacao_lote_w	timestamp;
dt_referencia_trunc_month_w	timestamp;
ie_permite_gerar_mens_contab_w	varchar(10);
ds_erro_w			varchar(255);


BEGIN

select	dt_mesano_referencia,
	dt_contabilizacao
into STRICT	dt_mesano_referencia_w,
	dt_contabilizacao_lote_w
from	pls_lote_mensalidade
where	nr_sequencia	= nr_seq_lote_p;

select	max(ie_permite_gerar_mens_contab)
into STRICT	ie_permite_gerar_mens_contab_w
from	pls_parametro_contabil
where	cd_estabelecimento	= cd_estabelecimento_p;

if (coalesce(ie_permite_gerar_mens_contab_w,'S') = 'N') then
	if (obter_se_lote_contabil_gerado(21,coalesce(dt_contabilizacao_lote_w,dt_mesano_referencia_w)) = 'S')
		or (obter_se_lote_contabil_gerado(49,coalesce(dt_contabilizacao_lote_w,dt_mesano_referencia_w)) = 'S')
		or (obter_se_lote_contabil_gerado(50,coalesce(dt_contabilizacao_lote_w,dt_mesano_referencia_w)) = 'S') then
		ds_erro_w	:= wheb_mensagem_pck.get_texto(221400, 'DT_REFERENCIA=' || to_char(coalesce(dt_contabilizacao_lote_w,dt_mesano_referencia_w),'dd/mm/yyyy'));
	end if;
end if;

dt_referencia_trunc_month_w	:= trunc(dt_mesano_referencia_w, 'month');

select	count(1)
into STRICT	qt_mes_competencia_w
from	pls_competencia
where	trunc(dt_mes_competencia,'month')	= dt_referencia_trunc_month_w
and	cd_estabelecimento			= cd_estabelecimento_p;

if (qt_mes_competencia_w = 0) then
	ds_erro_w	:= wheb_mensagem_pck.get_texto(176925);
end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
