CREATE OR REPLACE PROCEDURE pls_consist_parent_sca_benef ( nr_seq_vinculo_sca_p bigint, nr_seq_plano_p bigint, nr_seq_segurado_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
qt_regras_parentesco_w	bigint;
qt_registros_w		bigint;
nr_seq_parentesco_w	bigint;
ds_consistencia_w	varchar(255);
qt_idade_w		bigint;
			

BEGIN 
 
select	count(*) 
into STRICT	qt_regras_parentesco_w 
from	pls_plano_parentesco 
where	nr_seq_plano	= nr_seq_plano_p 
and	ie_situacao	= 'A';
 
if (qt_regras_parentesco_w > 0) then 
	select	nr_seq_parentesco, 
		obter_idade_pf(cd_pessoa_fisica,clock_timestamp(),'A') qt_idade 
	into STRICT	nr_seq_parentesco_w, 
		qt_idade_w 
	from	pls_segurado 
	where	nr_sequencia	= nr_seq_segurado_p;
 
	select	count(*) 
	into STRICT	qt_registros_w 
	from	pls_plano_parentesco 
	where	nr_seq_plano		= nr_seq_plano_p 
	and	nr_seq_parentesco	= nr_seq_parentesco_w 
	and	qt_idade_w between coalesce(qt_idade_min,0) and coalesce(qt_idade_max,999)	 
	and	ie_situacao	= 'A';
 
	if (qt_registros_w	= 0) then 
		ds_consistencia_w	:= 'O plano '|| substr(pls_obter_dados_produto(nr_seq_plano_p,'N'),1,255)|| ' não possui regras de parentesco para o beneficiário(a) '|| 
						substr(pls_obter_dados_segurado(nr_seq_segurado_p,'N'),1,255);
		update	pls_sca_vinculo 
		set	ds_consistencia	= ds_consistencia_w 
		where	nr_sequencia	= nr_seq_vinculo_sca_p;
	else 
		update	pls_sca_vinculo 
		set	ds_consistencia	 = NULL 
		where	nr_sequencia	= nr_seq_vinculo_sca_p;
	end if;
end if;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
