CREATE OR REPLACE PROCEDURE pls_consist_preco_unimed_fed ( nr_seq_lote_p pls_lote_preco_unimed_sc.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


qt_registro_w			integer;
vl_preco_w			pls_mat_uni_sc_pre_imp.vl_preco%type;
vl_preco_atual_w		pls_mat_uni_fed_sc_preco.vl_preco%type;
nr_sequencia_w			pls_mat_uni_sc_pre_imp.nr_sequencia%type;
cd_fornecedor_fed_sc_w		pls_mat_uni_sc_pre_imp.cd_fornecedor_fed_sc%type;
cd_material_w			pls_mat_uni_sc_pre_imp.cd_material%type;
cd_moeda_w			pls_mat_uni_sc_pre_imp.cd_moeda%type;
dt_preco_w			pls_mat_uni_sc_pre_imp.dt_preco%type;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		dt_preco,
		coalesce(vl_preco,0),
		cd_moeda,
		cd_fornecedor_fed_sc,
		cd_material
	from	pls_mat_uni_sc_pre_imp
	where	nr_seq_lote	= nr_seq_lote_p
	and	ie_situacao	= 'A'
	order by 1;


BEGIN
open C01;
loop
fetch C01 into
	nr_sequencia_w,
	dt_preco_w,
	vl_preco_w,
	cd_moeda_w,
	cd_fornecedor_fed_sc_w,
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	delete	FROM pls_mat_uni_sc_pre_incons
	where	nr_seq_mat_imp = nr_sequencia_w;

	update	pls_mat_uni_sc_pre_imp
	set	ie_inconsistente = 'N'
	where	nr_sequencia = nr_sequencia_w;

	select	max(coalesce(a.vl_preco,0))
	into STRICT	vl_preco_atual_w
	from	pls_mat_unimed_fed_sc b,
		pls_mat_uni_fed_sc_preco a
	where	a.nr_seq_mat_unimed	= b.nr_sequencia
	and	b.cd_material		= cd_material_w
	and	a.dt_preco		= dt_preco_w
	and	a.cd_fornecedor_fed_sc	= cd_fornecedor_fed_sc_w;

	if (vl_preco_w <> vl_preco_atual_w) then
		CALL pls_gerar_incon_pre_unimed_fed( nr_sequencia_w, cd_material_w, '1', nm_usuario_p);
	end if;

	select	count(1)
	into STRICT	qt_registro_w
	from	pls_mat_unimed_fed_sc
	where	cd_material	= cd_material_w;

	if (qt_registro_w = 0) then
		CALL pls_gerar_incon_pre_unimed_fed( nr_sequencia_w, cd_material_w, '2', nm_usuario_p);
	end if;
	end;
end loop;
close C01;

update	pls_lote_preco_unimed_sc
set	nm_usuario_consist	= nm_usuario_p
where 	nr_sequencia		= nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
