CREATE OR REPLACE PROCEDURE pls_consist_repas_abrang_conta ( nr_seq_segurado_p bigint, nr_seq_operadora_atend_p bigint, nm_usuario_p text, ie_area_coberta_p INOUT text, ie_tipo_conta_p text) AS $body$
DECLARE


qt_repasse_w				bigint;
ie_tipo_repasse_w			varchar(2);
nr_seq_congenere_w			bigint;
ie_atend_operadora_repasse_w		varchar(2);
nr_seq_plano_w				bigint;
ie_tipo_segurado_w			varchar(2);


BEGIN
ie_area_coberta_p	:= 'S';

-- Verificação de tipo de repasse do beneficiário e da regra de autorização
begin
	select	ie_tipo_repasse
	into STRICT	ie_tipo_repasse_w
	from	pls_segurado_repasse
	where	nr_seq_segurado	= nr_seq_segurado_p;

	select	nr_seq_plano
	into STRICT	nr_seq_plano_w
	from	pls_segurado
	where	nr_sequencia	= nr_seq_segurado_p;

	begin
	select	ie_tipo_segurado
	into STRICT	ie_tipo_segurado_w
	from	pls_segurado
	where	nr_sequencia = nr_seq_segurado_p;
	exception
	when others then
		ie_tipo_segurado_w	:= 'X';
	end;

	select	coalesce(ie_atend_operadora_repasse,'N')
	into STRICT	ie_atend_operadora_repasse_w
	from	pls_regra_intercambio_aut
	where	((coalesce(ie_tipo_repasse::text, '') = '')
	or (ie_tipo_repasse	= ie_tipo_repasse_w));
exception
when others then
	ie_atend_operadora_repasse_w	:= 'N';
end;

if (ie_atend_operadora_repasse_w	= 'S') then
	begin
	select	coalesce(nr_seq_congenere_atend,nr_seq_congenere)
	into STRICT	nr_seq_congenere_w
	from	pls_segurado_repasse
	where	nr_seq_segurado	= nr_seq_segurado_p;
	exception
	when others then
		nr_seq_congenere_w	:= 0;
	end;
	if (ie_tipo_conta_p = 'I')	then
		if (nr_seq_operadora_atend_p <> nr_seq_congenere_w)	then
			ie_area_coberta_p := 'N';
		end if;
	else
			ie_area_coberta_p := 'N';
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_consist_repas_abrang_conta ( nr_seq_segurado_p bigint, nr_seq_operadora_atend_p bigint, nm_usuario_p text, ie_area_coberta_p INOUT text, ie_tipo_conta_p text) FROM PUBLIC;

