CREATE OR REPLACE PROCEDURE pls_consist_vl_max_copar_event ( nr_seq_conta_p bigint, nr_seq_segurado_p bigint, nr_seq_regra_copart_p bigint, dt_mes_competencia_p timestamp, ie_periodo_valor_maximo_p text, vl_copart_cobrar_p bigint, vl_coparticipacao_p INOUT bigint, vl_tot_coparticipacao_p INOUT bigint) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Consiste o periodo de valor maximo da coparticipacao do tipo Evento(Todos itens)
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[ X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
				
vl_coparticipacao_w		double precision;
nr_seq_guia_w			pls_conta.nr_seq_guia%type;
cd_guia_ok_w			pls_conta.cd_guia_ok%type;


BEGIN

if (ie_periodo_valor_maximo_p = 'C') then
	select	sum(vl_coparticipacao)
	into STRICT	vl_coparticipacao_w
	from	pls_conta_coparticipacao
	where	nr_seq_regra	= nr_seq_regra_copart_p
	and	nr_seq_conta	= nr_seq_conta_p;
elsif (ie_periodo_valor_maximo_p = 'M') then
	select	sum(c.vl_coparticipacao)
	into STRICT	vl_coparticipacao_w
	from	pls_conta_coparticipacao	c,
		pls_conta			b,
		pls_protocolo_conta		a
	where	c.nr_seq_conta				= b.nr_sequencia
	and	b.nr_seq_protocolo			= a.nr_sequencia
	and	c.nr_seq_regra				= nr_seq_regra_copart_p
	and	b.nr_seq_segurado			= nr_seq_segurado_p
	and	trunc(a.dt_mes_competencia,'month')	= dt_mes_competencia_p;
elsif (ie_periodo_valor_maximo_p = 'A') then
	select	max(nr_seq_guia),
		max(cd_guia_ok)
	into STRICT	nr_seq_guia_w,
		cd_guia_ok_w
	from	pls_conta
	where	nr_sequencia	= nr_seq_conta_p;
	
	if (cd_guia_ok_w IS NOT NULL AND cd_guia_ok_w::text <> '') then
		select	sum(c.vl_coparticipacao)
		into STRICT	vl_coparticipacao_w
		from	pls_conta_coparticipacao	c,
			pls_conta			b
		where	c.nr_seq_conta				= b.nr_sequencia
		and	c.nr_seq_regra				= nr_seq_regra_copart_p
		and	b.nr_seq_segurado			= nr_seq_segurado_p
		and	b.cd_guia_ok				= cd_guia_ok_w;
	else
		select	sum(c.vl_coparticipacao)
		into STRICT	vl_coparticipacao_w
		from	pls_conta_coparticipacao	c,
			pls_conta			b
		where	c.nr_seq_conta				= b.nr_sequencia
		and	c.nr_seq_regra				= nr_seq_regra_copart_p
		and	b.nr_seq_segurado			= nr_seq_segurado_p
		and	b.nr_seq_guia				= nr_seq_guia_w;
	end if;
end if;

vl_coparticipacao_p	:= coalesce(vl_coparticipacao_w,0);
vl_tot_coparticipacao_p	:= coalesce(vl_coparticipacao_w,0) + coalesce(vl_copart_cobrar_p,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
