CREATE OR REPLACE PROCEDURE pls_consi_info_pre_rescisao_js ( nr_seq_segurado_p bigint, nr_seq_contrato_p bigint, cd_pessoa_fisica_p text, ds_erro_obito_p INOUT text, ds_aviso_contrato_p INOUT text, ds_erro_agravo_p INOUT text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
qt_registros_w		bigint;


BEGIN 
 
if (pls_obter_se_titular_obito(nr_seq_segurado_p,'N','P') = 'S') then 
	ds_erro_obito_p	:= substr(OBTER_TEXTO_DIC_OBJETO(245028,wheb_usuario_pck.get_nr_seq_idioma,''),1,225);
end if;
 
select	count(1) 
into STRICT	qt_registros_w 
from	pls_segurado		b, 
	pls_contrato		a 
where	b.nr_seq_contrato	= a.nr_sequencia 
and	a.nr_contrato_principal	= nr_seq_contrato_p 
and	b.cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	coalesce(b.dt_rescisao::text, '') = '';
 
if (qt_registros_w > 0) then 
	ds_aviso_contrato_p := substr(OBTER_TEXTO_DIC_OBJETO(111118,wheb_usuario_pck.get_nr_seq_idioma,''),1,225);
end if;
 
ds_erro_agravo_p := pls_consistir_agravo(null, nr_seq_segurado_p, cd_estabelecimento_p, nm_usuario_p, ds_erro_agravo_p);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
