CREATE OR REPLACE PROCEDURE pls_consulta_mala_diret_exclui ( ie_opcao_p text, nr_sequencias_p text, nm_usuario_p text) AS $body$
DECLARE


/*	ie_opcao_p
E - Excluir
L - Limpar Consulta
*/
BEGIN

if (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	begin
	if (ie_opcao_p = 'E') then
		begin
		if (nr_sequencias_p IS NOT NULL AND nr_sequencias_p::text <> '') then
			begin
			delete	from pls_mala_direta
			where	nr_sequencia in (nr_sequencias_p);
			end;
		end if;
		end;
	elsif (ie_opcao_p = 'L') then
		begin
		if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
			begin
			delete	from pls_mala_direta
			where	nm_usuario = nm_usuario_p;
			end;
		end if;
		end;
	end if;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
