SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_contabiliza_contestacao ( nr_lote_contabil_p bigint, nm_usuario_p text, ie_exclusao_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionario [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
-------------------------------------------------------------------------------------------------------------------

Referencias:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_conteudo_w				varchar(4000);
ds_erro_w				varchar(255);
ds_compl_historico_w			varchar(255);
ds_pessoa_w				varchar(80);
cd_conta_contabil_w			varchar(20)	:= null;
cd_cgc_w				varchar(14);
cd_pessoa_fisica_w			varchar(10);
ie_receber_pagar_w			varchar(1);
ie_compl_hist_w				varchar(1)	:= 'S';
vl_movimento_w				double precision;
vl_retorno_w				double precision;
nr_titulo_w				bigint;
nr_seq_trans_financ_w			bigint;
nr_tit_rec_gerado_w			bigint;
nr_tit_rec_baixado_w			bigint;
nr_seq_movto_w				bigint;
nr_tit_pagar_gerado_w			bigint;
nr_tit_pagar_baixado_w			bigint;
nr_sequencia_w				bigint;
cd_historico_pls_w			bigint;
cd_centro_custo_w			integer	:= 0;
cd_estab_movto_w			smallint;
cd_estabelecimento_w			smallint;
cd_tipo_lote_contabil_w			smallint;
dt_contab_w				timestamp;
dt_contabil_w				timestamp;
dt_contabil_mes_w			timestamp;
dt_contabil_fp_w			timestamp;
nm_atributo_w				w_movimento_contabil.nm_atributo%type;
nr_documento_ww				movimento_contabil.nr_documento%type;
ie_origem_documento_w			movimento_contabil.ie_origem_documento%type;
nr_seq_info_ctb_w			informacao_contabil.nr_sequencia%type;
nm_tabela_w				w_movimento_contabil.nm_tabela%type;
nm_atributo_ww				w_movimento_contabil.nm_atributo%type;
nr_seq_tab_orig_w			w_movimento_contabil.nr_seq_tab_orig%type;
nr_seq_tab_compl_w			w_movimento_contabil.nr_seq_tab_compl%type;
nm_agrupador_w				agrupador_contabil.nm_atributo%type;
nr_seq_agrupamento_w			w_movimento_contabil.nr_seq_agrupamento%type;
cd_tipo_recebimento_w			titulo_receber_liq.cd_tipo_recebimento%type;
ie_origem_tit_rec_w			titulo_receber.ie_origem_titulo%type;
cd_tipo_baixa_w				titulo_pagar_baixa.cd_tipo_baixa%type;
nr_seq_conta_banco_w			bigint;
nr_seq_pls_lote_contest_w		bigint;
nr_seq_pls_lote_disc_w			bigint;
nr_bordero_w				bordero_recebimento.nr_bordero%type;
nr_seq_cobranca_w			titulo_receber_liq.nr_seq_cobranca%type;

c_titulo CURSOR FOR
	SELECT	a.nr_titulo,
		'R' ie_receber_pagar,
		'VL_RECEBIDO' nm_atributo,
		sum(a.vl_recebido) vl_movimento,
		a.dt_recebimento dt_contab,
		a.nr_seq_trans_fin,
		b.cd_estabelecimento,
		'TITULO_RECEBER_LIQ' nm_tabela,
		'VL_RECEBIDO' nm_atrib,
		a.nr_sequencia nr_seq_tab_orig,
		b.nr_titulo nr_seq_tab_compl,
		14 nr_seq_info_ctb,
		a.nr_seq_conta_banco,
		a.cd_tipo_recebimento,
		b.ie_origem_titulo,
		null cd_tipo_baixa,
		b.nr_seq_pls_lote_contest,
		b.nr_seq_pls_lote_disc,
		a.nr_bordero,
		a.nr_seq_cobranca
	from	titulo_receber_liq	a,
		titulo_receber		b
	where	a.nr_titulo		= b.nr_titulo
	and	a.nr_lote_contabil	= nr_lote_contabil_p
	and	coalesce(a.ie_lib_caixa,'N')	= 'S'
	and	coalesce(a.vl_glosa,0)	= 0
	group by
		a.nr_titulo,
		a.dt_recebimento,
		a.nr_seq_trans_fin,
		b.cd_estabelecimento,
		a.nr_sequencia,
		b.nr_titulo,
		a.nr_seq_conta_banco,
		a.cd_tipo_recebimento,
		b.ie_origem_titulo,
		b.nr_seq_pls_lote_contest,
		b.nr_seq_pls_lote_disc,
		a.nr_bordero,
		a.nr_seq_cobranca
	
union all

	SELECT	a.nr_titulo,
		'R' ie_receber_pagar,
		'VL_REC_GLOSA' nm_atributo,
		sum(a.vl_glosa) vl_movimento,
		a.dt_recebimento dt_contab,
		a.nr_seq_trans_fin,
		b.cd_estabelecimento,
		'TITULO_RECEBER_LIQ' nm_tabela,
		'VL_GLOSA' nm_atrib,
		a.nr_sequencia nr_seq_tab_orig,
		a.nr_titulo nr_seq_tab_compl,
		14 nr_seq_info_ctb,
		a.nr_seq_conta_banco,
		a.cd_tipo_recebimento,
		b.ie_origem_titulo,
		null cd_tipo_baixa,
		b.nr_seq_pls_lote_contest,
		b.nr_seq_pls_lote_disc,
		a.nr_bordero,
		a.nr_seq_cobranca
	from	Titulo_rec_liq_cc	c,
		titulo_receber_liq	a,
		titulo_receber		b
	where	a.nr_sequencia		= c.nr_seq_baixa
	and	a.nr_titulo		= b.nr_titulo
	and	b.nr_titulo		= c.nr_titulo
	and	a.nr_lote_contabil	= nr_lote_contabil_p
	and	coalesce(a.ie_lib_caixa,'N')	= 'S'
	and	coalesce(a.vl_glosa,0)	<> 0
	group by
		a.nr_titulo,
		a.dt_recebimento,
		a.nr_seq_trans_fin,
		b.cd_estabelecimento,
		a.nr_sequencia,
		a.nr_seq_conta_banco,
		a.cd_tipo_recebimento,
		b.ie_origem_titulo,
		b.nr_seq_pls_lote_contest,
		b.nr_seq_pls_lote_disc,
		a.nr_bordero,
		a.nr_seq_cobranca
	
union all

	select	a.nr_titulo,
		'P' ie_receber_pagar,
		'VL_PAGO' nm_atributo,
		a.vl_baixa vl_movimento,
		a.dt_baixa dt_contab,
		a.nr_seq_trans_fin,
		b.cd_estabelecimento,
		'TITULO_PAGAR_BAIXA' nm_tabela,
		'VL_BAIXA' nm_atrib,
		a.nr_sequencia nr_seq_tab_orig,
		a.nr_titulo nr_seq_tab_compl,
		13 nr_seq_info_ctb,
		a.nr_seq_conta_banco,
		null cd_tipo_recebimento,
		null ie_origem_titulo,
		a.cd_tipo_baixa,
		b.nr_seq_pls_lote_contest,
		b.nr_seq_pls_lote_disc,
		a.nr_bordero,
		a.nr_seq_escrit
	from	titulo_pagar		b,
		titulo_pagar_baixa	a
	where	a.nr_titulo		= b.nr_titulo
	and	coalesce(b.ie_status,'D')	= 'D'
	and	a.nr_lote_contabil	= nr_lote_contabil_p
	and	coalesce(a.vl_glosa,0)	= 0
	
union all

	select	a.nr_titulo,
		'P' ie_receber_pagar,
		'VL_PAG_GLOSA' nm_atributo,
		a.vl_glosa vl_movimento,
		a.dt_baixa dt_contab,
		a.nr_seq_trans_fin,
		b.cd_estabelecimento,
		'TITULO_PAGAR_BAIXA' nm_tabela,
		'VL_GLOSA' nm_atrib,
		a.nr_sequencia nr_seq_tab_orig,
		a.nr_titulo nr_seq_tab_compl,
		13 nr_seq_info_ctb,
		a.nr_seq_conta_banco,
		null cd_tipo_recebimento,
		null ie_origem_titulo,
		a.cd_tipo_baixa,
		b.nr_seq_pls_lote_contest,
		b.nr_seq_pls_lote_disc,
		a.nr_bordero,
		a.nr_seq_escrit
	from	titulo_pagar		b,
		titulo_pagar_baixa	a
	where	a.nr_titulo		= b.nr_titulo
	and	coalesce(b.ie_status,'D')	= 'D'
	and	a.nr_lote_contabil	= nr_lote_contabil_p
	and	coalesce(a.vl_glosa,0)	<> 0
	
union all

	select	a.nr_titulo,
		'R' ie_receber_pagar,
		'VL_TITULO_RECEBER' nm_atributo,
		a.vl_titulo vl_movimento,
		a.dt_contabil dt_contab,
		a.nr_seq_trans_fin_contab,
		a.cd_estabelecimento,
		'TITULO_RECEBER' nm_tabela,
		'VL_TITULO' nm_atrib,
		a.nr_titulo nr_seq_tab_orig,
		null nr_seq_tab_compl,
		46 nr_seq_info_ctb,
		null nr_seq_conta_banco,
		null cd_tipo_recebimento,
		a.ie_origem_titulo,
		null cd_tipo_baixa,
		a.nr_seq_pls_lote_contest,
		a.nr_seq_pls_lote_disc,
		null,
		null
	from	titulo_receber	a
	where	a.nr_lote_contabil	= nr_lote_contabil_p
	
union all

	select	a.nr_titulo,
		'P' ie_receber_pagar,
		'VL_TITULO_PAGAR' nm_atributo,
		a.vl_titulo vl_movimento,
		a.dt_contabil dt_contab,
		a.nr_seq_trans_fin_contab,
		a.cd_estabelecimento,
		'TITULO_PAGAR' nm_tabela,
		'VL_TITULO' nm_atrib,
		a.nr_titulo nr_seq_tab_orig,
		null nr_seq_tab_compl,
		47 nr_seq_info_ctb,
		null nr_seq_conta_banco,
		null cd_tipo_recebimento,
		null ie_origem_titulo,
		null cd_tipo_baixa,
		a.nr_seq_pls_lote_contest,
		a.nr_seq_pls_lote_disc,
		a.nr_bordero,
		null
	from	titulo_pagar	a
	where	a.nr_lote_contabil	= nr_lote_contabil_p
	and	coalesce(a.ie_status,'D')	= 'D'
	
union all

	select	a.nr_titulo,
		'R' ie_receber_pagar,
		'VL_REC_MAIOR' nm_atributo,
		a.vl_rec_maior vl_movimento,
		a.dt_recebimento dt_contab,
		a.nr_seq_trans_fin,
		b.cd_estabelecimento,
		'TITULO_RECEBER_LIQ' nm_tabela,
		'VL_REC_MAIOR' nm_atrib,
		a.nr_sequencia nr_seq_tab_orig,
		b.nr_titulo nr_seq_tab_compl,
		14 nr_seq_info_ctb,
		a.nr_seq_conta_banco,
		a.cd_tipo_recebimento,
		b.ie_origem_titulo,
		null cd_tipo_baixa,
		b.nr_seq_pls_lote_contest,
		b.nr_seq_pls_lote_disc,
		a.nr_bordero,
		a.nr_seq_cobranca
	from	titulo_receber_liq	a,
		titulo_receber		b
	where	a.nr_titulo		= b.nr_titulo
	and	coalesce(a.ie_lib_caixa,'N')	= 'S'
	and	coalesce(a.vl_glosa,0)	= 0
	and	coalesce(a.vl_rec_maior,0) 	<> 0
	and	a.nr_lote_contabil	= nr_lote_contabil_p;
	
c_movimento CURSOR FOR
	SELECT	oid
	from	movimento_contabil
	where	nr_lote_contabil	= nr_lote_contabil_p;
	
c_movimento_w CURSOR FOR
	SELECT	oid
	from	w_movimento_contabil
	where	nr_lote_contabil	= nr_lote_contabil_p;
	
BEGIN
/*Validacao para impedir a geracao em lotes incorretos */

if (ie_exclusao_p <> 'S') then
        select b.cd_tipo_lote_contabil
        into STRICT cd_tipo_lote_contabil_w
        from lote_contabil b
        where b.nr_lote_contabil = nr_lote_contabil_p;
        if (cd_tipo_lote_contabil_w <> 37) then
                CALL wheb_mensagem_pck.exibir_mensagem_abort(261346);
        end if;
end if;
select	dt_referencia,
	cd_estabelecimento,
	cd_tipo_lote_contabil
into STRICT 	dt_contabil_w,
	cd_estabelecimento_w,
	cd_tipo_lote_contabil_w
from 	lote_contabil
where 	nr_lote_contabil 	= nr_lote_contabil_p;

if (ie_exclusao_p = 'S') then
	CALL wheb_usuario_pck.set_ie_lote_contabil('S');
	
	for reg in c_movimento
		loop
		delete	from movimento_contabil
		where   rowid	= reg.rowid;
		end loop;

	update	lote_contabil
	set	vl_credito 		= 0,
		vl_debito  		= 0
	where	nr_lote_contabil 	= nr_lote_contabil_p;
	
	commit;
	
	update	titulo_pagar_baixa a
	set	a.nr_lote_contabil 	= 0
	where 	a.nr_lote_contabil 	= nr_lote_contabil_p;
	
	commit;

	update	titulo_receber_liq a
	set	a.nr_lote_contabil	= 0
	where 	a.nr_lote_contabil 	= nr_lote_contabil_p;
	
	commit;
	
	update	titulo_receber	a
	set	a.nr_lote_contabil	= 0
	where	a.nr_lote_contabil	= nr_lote_contabil_p;
	
	commit;
	
	update	titulo_pagar	a
	set	a.nr_lote_contabil	= 0
	where	a.nr_lote_contabil	= nr_lote_contabil_p;
	
	commit;
	
	CALL wheb_usuario_pck.set_ie_lote_contabil('N');
else
	dt_contabil_mes_w	:= trunc(dt_contabil_w, 'month');
	dt_contabil_fp_w	:= fim_dia(dt_contabil_w);
	
	CALL wheb_usuario_pck.set_ie_lote_contabil('S');
	
	for reg in c_movimento_w
		loop
		delete	from w_movimento_contabil
		where   rowid	= reg.rowid;
		end loop;
	
	update	titulo_receber_liq	a
	set	a.nr_lote_contabil		= nr_lote_contabil_p
	where	a.dt_recebimento between dt_contabil_mes_w and dt_contabil_fp_w
	and	coalesce(a.nr_seq_pls_lote_camara,0) = 0
	and	coalesce(a.nr_seq_lote_enc_contas,0) = 0
	and	exists	(SELECT	1
			from	titulo_receber	x
			where	a.nr_titulo		= x.nr_titulo
			and	x.ie_origem_titulo	= '11'
			and	((x.nr_seq_pls_lote_contest IS NOT NULL AND x.nr_seq_pls_lote_contest::text <> '') or (x.nr_seq_pls_lote_disc IS NOT NULL AND x.nr_seq_pls_lote_disc::text <> '')))
	and	coalesce(a.nr_lote_contabil,0)	= 0;
	
	commit;
	
	update	titulo_pagar_baixa	a
	set	a.nr_lote_contabil		= nr_lote_contabil_p
	where	a.dt_baixa between dt_contabil_mes_w and dt_contabil_fp_w
	and	coalesce(a.nr_seq_pls_lote_camara,0) = 0
	and	coalesce(a.nr_seq_lote_enc_contas,0) = 0
	and	exists	(SELECT	1
			from	titulo_pagar	x
			where	x.nr_titulo		= a.nr_titulo
			and	x.ie_origem_titulo	= '19'
			and	((x.nr_seq_pls_lote_contest IS NOT NULL AND x.nr_seq_pls_lote_contest::text <> '') or (x.nr_seq_pls_lote_disc IS NOT NULL AND x.nr_seq_pls_lote_disc::text <> '')))
	and	coalesce(a.nr_lote_contabil,0)	= 0;
	
	commit;
	
	update	titulo_receber	a
	set	a.nr_lote_contabil		= nr_lote_contabil_p
	where	a.dt_contabil between dt_contabil_mes_w and dt_contabil_fp_w
	and	a.ie_origem_titulo		= '11'
	and	((a.nr_seq_pls_lote_contest IS NOT NULL AND a.nr_seq_pls_lote_contest::text <> '') or (a.nr_seq_pls_lote_disc IS NOT NULL AND a.nr_seq_pls_lote_disc::text <> ''))
	and	coalesce(a.nr_lote_contabil,0)	= 0
	and 	a.ie_situacao 			<> 3;
	
	commit;
	
	update	titulo_pagar	a
	set	a.nr_lote_contabil		= nr_lote_contabil_p
	where	a.dt_contabil between dt_contabil_mes_w and dt_contabil_fp_w
	and	a.ie_origem_titulo		= '19'
	and	((a.nr_seq_pls_lote_contest IS NOT NULL AND a.nr_seq_pls_lote_contest::text <> '') or (a.nr_seq_pls_lote_disc IS NOT NULL AND a.nr_seq_pls_lote_disc::text <> ''))
	and	a.ie_situacao			<> 'C'
	and	coalesce(a.nr_lote_contabil,0)	= 0;
	
	commit;
	
	CALL wheb_usuario_pck.set_ie_lote_contabil('N');
	
	begin
	ie_compl_hist_w	:= obter_se_compl_tipo_lote(cd_estabelecimento_w, cd_tipo_lote_contabil_w);
	exception
	when others then
		ie_compl_hist_w	:= 'N';
	end;
	
	nm_agrupador_w := coalesce(trim(both obter_agrupador_contabil(cd_tipo_lote_contabil_w)),'NR_TITULO');
	
	open c_titulo;
	loop
	fetch c_titulo into	
		nr_titulo_w,
		ie_receber_pagar_w,
		nm_atributo_w,
		vl_movimento_w,
		dt_contab_w,
		nr_seq_trans_financ_w,
		cd_estab_movto_w,
		nm_tabela_w,
		nm_atributo_ww,
		nr_seq_tab_orig_w,
		nr_seq_tab_compl_w,
		nr_seq_info_ctb_w,
		nr_seq_conta_banco_w,
		cd_tipo_recebimento_w,
		ie_origem_tit_rec_w,
		cd_tipo_baixa_w,
		nr_seq_pls_lote_contest_w,
		nr_seq_pls_lote_disc_w,
		nr_bordero_w,
		nr_seq_cobranca_w;
	EXIT WHEN NOT FOUND; /* apply on c_titulo */
		begin
		
		nr_tit_pagar_gerado_w	:= null;	
		nr_tit_rec_gerado_w	:= null;
		ds_pessoa_w		:= null;
		
		if (ie_receber_pagar_w = 'R') then
			nr_tit_rec_gerado_w	:= nr_titulo_w;
			
			select	substr(obter_nome_pf_pj(a.cd_pessoa_fisica,a.cd_cgc),1,80)
			into STRICT	ds_pessoa_w
			from	titulo_receber a
			where	a.nr_titulo	= nr_titulo_w;
		elsif (ie_receber_pagar_w = 'P') then
			nr_tit_pagar_gerado_w	:= nr_titulo_w;
		
			select	substr(obter_nome_pf_pj(a.cd_pessoa_fisica,a.cd_cgc),1,80),
				a.cd_pessoa_fisica,
				a.cd_cgc
			into STRICT	ds_pessoa_w,
				cd_pessoa_fisica_w,
				cd_cgc_w
			from	titulo_pagar	a
			where	a.nr_titulo	= nr_titulo_w;
		end if;
		
		ds_conteudo_w		:= '';
		
		if (ie_compl_hist_w = 'S') then
			ds_conteudo_w	:= substr(	nr_tit_pagar_gerado_w				|| '#@' ||
							nr_tit_rec_gerado_w				|| '#@' ||
							nr_seq_pls_lote_contest_w			|| '#@' ||
							nr_seq_pls_lote_disc_w				|| '#@' ||
							ds_pessoa_w					|| '#@' ||
							nr_bordero_w					|| '#@' ||
							nr_seq_cobranca_w, 1, 4000);
		
			begin
			ds_compl_historico_w	:= substr(obter_compl_historico(cd_tipo_lote_contabil_w, cd_historico_pls_w, ds_conteudo_w),1,255);
			exception
			when others then
				ds_compl_historico_w	:= null;
			end;
		end if;
		
		if (ie_receber_pagar_w = 'P') then
			nr_documento_ww		:= nr_titulo_w;
			ie_origem_documento_w	:= 2;
		else
			nr_documento_ww		:= nr_titulo_w;
			ie_origem_documento_w	:= 3;	
		end if;
		
		nr_seq_agrupamento_w	:= null;
		
		if (nm_agrupador_w = 'NR_TITULO') then
			nr_seq_agrupamento_w := somente_numero(substr(nr_titulo_w, 1, 10));
		end if;
		
		nr_seq_movto_w := gerar_contab_trans_financ(	cd_estabelecimento_w, cd_estab_movto_w, nr_lote_contabil_p, nm_usuario_p, cd_conta_contabil_w, cd_centro_custo_w, null, nr_seq_agrupamento_w, dt_contab_w, vl_movimento_w, nr_seq_trans_financ_w, nr_seq_conta_banco_w, nm_atributo_w, null, null, 0, null, nr_documento_ww, null, ds_conteudo_w, null, null, null, null, nr_tit_pagar_gerado_w, null, null, ie_origem_tit_rec_w, null, null, null, null, cd_tipo_baixa_w, cd_tipo_recebimento_w, nr_seq_movto_w, nm_tabela_w, nr_tit_rec_gerado_w, null, ie_origem_documento_w, nr_seq_tab_orig_w, nr_seq_tab_compl_w, nr_seq_info_ctb_w);
		end;
	end loop;
	close c_titulo;
	
	CALL agrupa_movimento_contabil(	nr_lote_contabil_p,
					nm_usuario_p);
end if;

if (coalesce(ds_retorno_p::text, '') = '') then
	begin
	update	lote_contabil
	set	ie_situacao		= 'A',
		dt_geracao_lote		= CASE WHEN ie_exclusao_p='N' THEN clock_timestamp() WHEN ie_exclusao_p='S' THEN null END
	where	nr_lote_contabil	= nr_lote_contabil_p;
	
      	if (ie_exclusao_p = 'S') then
		ds_retorno_p	:= wheb_mensagem_pck.get_texto(165188);
		
		CALL ctb_gravar_log_lote(	nr_lote_contabil_p,
					2,
					'',
					nm_usuario_p);
	else
		ds_retorno_p	:= wheb_mensagem_pck.get_texto(165187);
		
		CALL ctb_gravar_log_lote(	nr_lote_contabil_p,
					1,
					wheb_mensagem_pck.get_texto(1097175),
					nm_usuario_p);
	end if;
	
	commit;
	end;
else
	rollback;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_contabiliza_contestacao ( nr_lote_contabil_p bigint, nm_usuario_p text, ie_exclusao_p text, ds_retorno_p INOUT text) FROM PUBLIC;

