CREATE OR REPLACE PROCEDURE pls_conta_atual_benef_carteira ( nr_seq_conta_p bigint, nr_seq_segurado_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	update	pls_conta
	set	nr_seq_segurado	= nr_seq_segurado_p
	where	nr_sequencia	= nr_seq_conta_p;

	insert into pls_conta_historico(nr_sequencia, dt_atualizacao, nm_usuario,
		dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_conta,
		dt_historico, ds_titulo, ds_historico)
	values (nextval('pls_conta_historico_seq'), clock_timestamp(), nm_usuario_p,
		clock_timestamp(), nm_usuario_p, nr_seq_conta_p,
		clock_timestamp(), 'Alterado o benefici√°rio da conta', '');

	update	pls_conta_medica_resumo
	set	nr_seq_segurado	= nr_seq_segurado_p
	where	nr_seq_conta	= nr_seq_conta_p
	and	ie_situacao = 'A';
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
