CREATE OR REPLACE PROCEDURE pls_conta_atual_medico_exec ( nr_seq_conta_p bigint, cd_medico_exec_p text, nm_usuario_p text) AS $body$
DECLARE

ie_status_w		varchar(1);
nr_crm_exec_w		varchar(20);
nr_seq_cbo_saude_w	bigint;
uf_crm_exec_w		varchar(2);
nr_seq_conselho_exec_w	bigint;


BEGIN 
 
if (cd_medico_exec_p IS NOT NULL AND cd_medico_exec_p::text <> '') then 
	 
	select 	substr(obter_crm_medico(cd_medico_exec_p),1,255), 
		substr(obter_dados_medico(cd_medico_exec_p, 'UFCRM'),1,255), 
		substr(pls_obter_seq_conselho_prof(cd_medico_exec_p),1,255) 
	into STRICT	nr_crm_exec_w, 
		uf_crm_exec_w, 
		nr_seq_conselho_exec_w 
	;
	 
	select max(nr_seq_cbo_saude) 
	into STRICT	nr_seq_cbo_saude_w 
    from  medico_especialidade 
    where  cd_pessoa_fisica = cd_medico_exec_p 
    and   nr_seq_prioridade = (	SELECT  min(nr_seq_prioridade) 
					from   medico_especialidade 
                    where  cd_pessoa_fisica = cd_medico_exec_p ) 
    and  (nr_seq_cbo_saude IS NOT NULL AND nr_seq_cbo_saude::text <> '');
																   
	update	pls_conta 
	set	cd_medico_executor 	= cd_medico_exec_p, 
		nr_crm_exec 		= nr_crm_exec_w, 
		nr_seq_cbo_saude	= nr_seq_cbo_saude_w, 
		uf_crm_exec		= uf_crm_exec_w, 
		nr_seq_conselho_exec	= nr_seq_conselho_exec_w, 
		nm_usuario 		= nm_usuario_p 
	where 	nr_sequencia 		= nr_seq_conta_p;
	 
	insert into pls_conta_historico(nr_sequencia, dt_atualizacao, nm_usuario, 
		dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_conta, 
		dt_historico, ds_titulo, ds_historico) 
	values (nextval('pls_conta_historico_seq'), clock_timestamp(), nm_usuario_p, 
		clock_timestamp(), nm_usuario_p, nr_seq_conta_p, 
		clock_timestamp(), 'Alterado o m√©dico executor da conta', '');
end if;
 
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
