CREATE OR REPLACE PROCEDURE pls_control_bio_med_consult ( cd_medico_p pessoa_fisica.cd_pessoa_fisica%type, nr_seq_usuario_web_p pls_usuario_web.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Salvar biometria do medico ao se identificar no consultorio.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[  ]  Objetos do dicionario [ ] Tasy (Delphi/Java) [ x] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
									 
nr_seq_controle_bio_med_w		pls_controle_biometria_med.nr_sequencia%type;
ie_permite_bio_cosult_w			varchar(1);


BEGIN

select 	max(nr_sequencia)
into STRICT	nr_seq_controle_bio_med_w
from 	pls_controle_biometria_med 
where 	cd_medico	= cd_medico_p
and	(dt_check_in IS NOT NULL AND dt_check_in::text <> '')	
and	coalesce(dt_check_out::text, '') = '';


if (coalesce(nr_seq_controle_bio_med_w::text, '') = '') then

	ie_permite_bio_cosult_w	:= pls_obter_permite_bio_consult(cd_medico_p);

	if (ie_permite_bio_cosult_w = 'S') then
		insert into pls_controle_biometria_med(	nr_sequencia, nm_usuario, nm_usuario_nrec,
								dt_atualizacao, dt_atualizacao_nrec, cd_estabelecimento, 	
								dt_check_in, cd_medico, nr_seq_usuario_web_check_in)
						values (	nextval('pls_controle_biometria_med_seq'), nm_usuario_p, nm_usuario_p,
								clock_timestamp(), clock_timestamp(), cd_estabelecimento_p,
								clock_timestamp(), cd_medico_p, nr_seq_usuario_web_p);	
	end if;
else
	update	pls_controle_biometria_med
	set	dt_check_out			= clock_timestamp(),
		nr_seq_usuario_web_check_out 	= nr_seq_usuario_web_p,
		nm_usuario			= nm_usuario_p,
		dt_atualizacao			= clock_timestamp()
	where	nr_sequencia			= nr_seq_controle_bio_med_w
	and	(dt_check_in IS NOT NULL AND dt_check_in::text <> '');
end if;
     		
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
